import{_ as e,o as i,c as a,O as t}from"./chunks/framework.9482e208.js";const m=JSON.parse('{"title":"§ 15.2-965 Human rights ordinances and commissions.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/15.2-965.md","filePath":"vacode/15.2-965.md"}'),n={name:"vacode/15.2-965.md"},r=t('<h1 id="§-15-2-965-human-rights-ordinances-and-commissions" tabindex="-1">§ 15.2-965 Human rights ordinances and commissions. <a class="header-anchor" href="#§-15-2-965-human-rights-ordinances-and-commissions" aria-label="Permalink to &quot;§ 15.2-965 Human rights ordinances and commissions.&quot;">​</a></h1><p>A. Any locality may enact an ordinance, not inconsistent with nor more stringent than any applicable state law, prohibiting discrimination in housing, employment, public accommodations, credit, and education on the basis of race, color, religion, sex, pregnancy, childbirth or related medical conditions, national origin, military status, age, marital status, sexual orientation, gender identity, or disability.</p><p>B. The locality may enact an ordinance establishing a local commission on human rights that shall have the powers and duties granted by the Virginia Human Rights Act (§ <a href="/vacode/2.2-3900/">2.2-3900</a> et seq.).</p><p>C. As used in this section:</p><p>&quot;Gender identity&quot; means the gender-related identity, appearance, or other gender-related characteristics of an individual, without regard to the individual&#39;s designated sex at birth.</p><p>&quot;Military status&quot; means status as (i) a member of the uniformed forces, as defined in 10 U.S.C. § 101(a)(5), of the United States or a reserve component thereof named under 10 U.S.C. § 10101, (ii) a veteran as defined in 38 U.S.C. § 101(2), or (iii) a dependent as defined in 50 U.S.C. § 3911(4) except that the support provided by the service member to the individual shall have been provided 180 days immediately preceding an alleged action that if proven true would constitute unlawful discrimination under this section instead of 180 days immediately preceding an application for relief under 50 U.S.C. Chapter 50.</p><p>&quot;Religion&quot; includes any outward expression of religious faith, including adherence to religious dressing and grooming practices and the carrying or display of religious items or symbols.</p><p>&quot;Sexual orientation&quot; means a person&#39;s actual or perceived heterosexuality, bisexuality, or homosexuality.</p><p>1987, c. 569, § 15.1-37.3:8; 1997, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?971+ful+CHAP0404" target="_blank" rel="noreferrer">404</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?971+ful+CHAP0587" target="_blank" rel="noreferrer">587</a>; 2020, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?201+ful+CHAP0131" target="_blank" rel="noreferrer">131</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?201+ful+CHAP1137" target="_blank" rel="noreferrer">1137</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?201+ful+CHAP1140" target="_blank" rel="noreferrer">1140</a>; 2021, Sp. Sess. I, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?212+ful+CHAP0477" target="_blank" rel="noreferrer">477</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?212+ful+CHAP0478" target="_blank" rel="noreferrer">478</a>; 2022, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?221+ful+CHAP0799" target="_blank" rel="noreferrer">799</a>.</p>',9),o=[r];function s(l,d,c,h,g,p){return i(),a("div",null,o)}const f=e(n,[["render",s]]);export{m as __pageData,f as default};
