import{_ as e,o,c as a,O as r}from"./chunks/framework.9482e208.js";const m=JSON.parse('{"title":"§ 15.2-973 Ordinances imposing license taxes on owners of certain motor vehicles.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/15.2-973.md","filePath":"vacode/15.2-973.md"}'),t={name:"vacode/15.2-973.md"},n=r('<h1 id="§-15-2-973-ordinances-imposing-license-taxes-on-owners-of-certain-motor-vehicles" tabindex="-1">§ 15.2-973 Ordinances imposing license taxes on owners of certain motor vehicles. <a class="header-anchor" href="#§-15-2-973-ordinances-imposing-license-taxes-on-owners-of-certain-motor-vehicles" aria-label="Permalink to &quot;§ 15.2-973 Ordinances imposing license taxes on owners of certain motor vehicles.&quot;">​</a></h1><p>Any locality may adopt an ordinance imposing a license tax, in an amount not exceeding $100 annually, upon the owners of motor vehicles that do not display current license plates and that are not exempted from the requirements of displaying such license plates under the provisions of Article 6 (§ <a href="http://law.lis.virginia.gov/vacode/46.2-662/" target="_blank" rel="noreferrer">46.2-662</a> et seq.) of Chapter 6 of Title 46.2, §§ <a href="http://law.lis.virginia.gov/vacode/46.2-1554/" target="_blank" rel="noreferrer">46.2-1554</a> and <a href="http://law.lis.virginia.gov/vacode/46.2-1555/" target="_blank" rel="noreferrer">46.2-1555</a>, are not in a public dump, in an &quot;automobile graveyard&quot; as defined in § <a href="http://law.lis.virginia.gov/vacode/33.2-804/" target="_blank" rel="noreferrer">33.2-804</a>, or in the possession of a licensed junk dealer or licensed motor vehicle dealer. Nothing in this section shall be applicable to any vehicle being held or stored by or at the direction of any governmental authority, to any vehicle owned by a member of the armed forces on active duty or to any vehicle regularly stored within a structure. Nothing in this section shall be applicable to motor vehicles that are stored on private property for the purpose of restoration or repair or for removing parts for the repair of another vehicle.</p><p>1970, c. 380, § 15.1-27.1; 1977, c. 557; 1982, c. 216; 1988, c. 484; 1997, c. 587; 2013, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?131+ful+CHAP0347" target="_blank" rel="noreferrer">347</a>.</p>',3),i=[n];function s(c,l,h,d,p,f){return o(),a("div",null,i)}const _=e(t,[["render",s]]);export{m as __pageData,_ as default};
