import{_ as t,o as a,c as r,z as e,a as o}from"./chunks/framework.9482e208.js";const v=JSON.parse('{"title":"§ 15.2-975 Use of cash proffers.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/15.2-975.md","filePath":"vacode/15.2-975.md"}'),s={name:"vacode/15.2-975.md"},n=e("h1",{id:"§-15-2-975-use-of-cash-proffers",tabindex:"-1"},[o("§ 15.2-975 Use of cash proffers. "),e("a",{class:"header-anchor",href:"#§-15-2-975-use-of-cash-proffers","aria-label":'Permalink to "§ 15.2-975 Use of cash proffers."'},"​")],-1),i=e("p",null,"Localities which are authorized to accept voluntary cash proffers may also issue bonds under the provisions of the Public Finance Act and other applicable law including local charters, to finance improvements contained in the construction improvement program, to the extent that the costs of such improvements have been pledged by landowners as voluntary cash proffers. Authorized localities may pledge the proceeds of such proffers as a specific undertaking from which revenue is derived pursuant to Article VII, Section 10 (a) (3) of the Constitution of Virginia. The use of pledged cash proffers to finance improvements shall be limited to the improvements or class of improvements for which the proffer was originally pledged, and all or any part of the total amount pledged through the conditional zoning process may be further pledged by the locality to support repayment of any such debt.",-1),c=e("p",null,[o("2004, c. "),e("a",{href:"http://lis.virginia.gov/cgi-bin/legp604.exe?041+ful+CHAP0927",target:"_blank",rel:"noreferrer"},"927"),o(".")],-1),l=[n,i,c];function h(f,p,d,u,m,_){return a(),r("div",null,l)}const b=t(s,[["render",h]]);export{v as __pageData,b as default};
