import{_ as e,o as r,c as t,O as a}from"./chunks/framework.9482e208.js";const u=JSON.parse('{"title":"§ 16.1-241.3 Newborn children; substance abuse.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/16.1-241.3.md","filePath":"vacode/16.1-241.3.md"}'),i={name:"vacode/16.1-241.3.md"},n=a('<h1 id="§-16-1-241-3-newborn-children-substance-abuse" tabindex="-1">§ 16.1-241.3 Newborn children; substance abuse. <a class="header-anchor" href="#§-16-1-241-3-newborn-children-substance-abuse" aria-label="Permalink to &quot;§ 16.1-241.3 Newborn children; substance abuse.&quot;">​</a></h1><p>Upon the filing of a petition alleging that an investigation has been commenced in response to a report of suspected abuse or neglect of the child based upon a factor specified in subsection B of § <a href="http://law.lis.virginia.gov/vacode/63.2-1509/" target="_blank" rel="noreferrer">63.2-1509</a>, the court may enter any order authorized pursuant to this chapter which the court deems necessary to protect the health and welfare of the child pending final disposition of the investigation pursuant to Chapter 15 (§ <a href="http://law.lis.virginia.gov/vacode/63.2-1500/" target="_blank" rel="noreferrer">63.2-1500</a> et seq.) of Title 63.2 or other proceedings brought pursuant to this chapter. Such orders may include, but shall not be limited to, an emergency removal order pursuant to § <a href="http://law.lis.virginia.gov/vacode/16.1-251/" target="_blank" rel="noreferrer">16.1-251</a>, a preliminary protective order pursuant to § <a href="http://law.lis.virginia.gov/vacode/16.1-253/" target="_blank" rel="noreferrer">16.1-253</a> or an order authorized pursuant to subdivisions A 1 through 4 of § <a href="http://law.lis.virginia.gov/vacode/16.1-278.2/" target="_blank" rel="noreferrer">16.1-278.2</a>. The fact that an order was entered pursuant to this section shall not be admissible as evidence in any criminal, civil or administrative proceeding other than a proceeding to enforce the order.</p><p>The order shall be effective for a limited duration not to exceed the period of time necessary to conclude the investigation and any proceedings initiated pursuant to Chapter 15 (§ <a href="http://law.lis.virginia.gov/vacode/63.2-1500/" target="_blank" rel="noreferrer">63.2-1500</a> et seq.) of Title 63.2, but shall be a final order subject to appeal.</p><p>1998, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?981+ful+CHAP0704" target="_blank" rel="noreferrer">704</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?981+ful+CHAP0716" target="_blank" rel="noreferrer">716</a>; 2002, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?021+ful+CHAP0860" target="_blank" rel="noreferrer">860</a>; 2012, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?121+ful+CHAP0504" target="_blank" rel="noreferrer">504</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?121+ful+CHAP0640" target="_blank" rel="noreferrer">640</a>.</p>',4),o=[n];function s(l,c,h,d,p,g){return r(),t("div",null,o)}const b=e(i,[["render",s]]);export{u as __pageData,b as default};
