import{_ as e,o as a,c as t,O as s}from"./chunks/framework.9482e208.js";const m=JSON.parse('{"title":"§ 16.1-248.2 Mental health screening and assessment for certain juveniles.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/16.1-248.2.md","filePath":"vacode/16.1-248.2.md"}'),n={name:"vacode/16.1-248.2.md"},r=s('<h1 id="§-16-1-248-2-mental-health-screening-and-assessment-for-certain-juveniles" tabindex="-1">§ 16.1-248.2 Mental health screening and assessment for certain juveniles. <a class="header-anchor" href="#§-16-1-248-2-mental-health-screening-and-assessment-for-certain-juveniles" aria-label="Permalink to &quot;§ 16.1-248.2 Mental health screening and assessment for certain juveniles.&quot;">​</a></h1><p>Whenever a juvenile is placed in a secure facility pursuant to § <a href="http://law.lis.virginia.gov/vacode/16.1-248.1/" target="_blank" rel="noreferrer">16.1-248.1</a>, the staff of the facility shall gather such information from the juvenile and the probation officer as is reasonably available and deemed necessary by the facility staff. As part of the intake procedures at each such facility, the staff shall ascertain the juvenile&#39;s need for a mental health assessment. If it is determined that the juvenile needs such an assessment, the assessment shall take place within twenty-four hours of such determination. The community services board serving the jurisdiction where the facility is located shall be responsible for conducting the assessments and shall be compensated from funds appropriated to the Department of Juvenile Justice for this purpose. The Department of Juvenile Justice shall develop criteria and a compensation plan for such assessments.</p><p>1996, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?961+ful+CHAP0755" target="_blank" rel="noreferrer">755</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?961+ful+CHAP0914" target="_blank" rel="noreferrer">914</a>; 1998, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?981+ful+CHAP0434" target="_blank" rel="noreferrer">434</a>.</p>',3),i=[r];function l(o,c,h,f,d,p){return a(),t("div",null,i)}const _=e(n,[["render",l]]);export{m as __pageData,_ as default};
