import{_ as e,o as a,c as i,O as n}from"./chunks/framework.9482e208.js";const v=JSON.parse('{"title":"§ 16.1-255 Limitation on issuance of detention orders for juveniles; appearance by juvenile.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/16.1-255.md","filePath":"vacode/16.1-255.md"}'),r={name:"vacode/16.1-255.md"},o=n('<h1 id="§-16-1-255-limitation-on-issuance-of-detention-orders-for-juveniles-appearance-by-juvenile" tabindex="-1">§ 16.1-255 Limitation on issuance of detention orders for juveniles; appearance by juvenile. <a class="header-anchor" href="#§-16-1-255-limitation-on-issuance-of-detention-orders-for-juveniles-appearance-by-juvenile" aria-label="Permalink to &quot;§ 16.1-255 Limitation on issuance of detention orders for juveniles; appearance by juvenile.&quot;">​</a></h1><p>No detention order shall be issued for any juvenile except when authorized by the judge or intake officer of a juvenile court or by a magistrate as provided in § <a href="http://law.lis.virginia.gov/vacode/16.1-256/" target="_blank" rel="noreferrer">16.1-256</a>.</p><p>In matters involving the issuance of detention orders each state or local court service unit shall ensure the capability of a prompt response by an intake officer who is either on duty or on call.</p><p>A child may appear before an intake officer either (i) by personal appearance before the intake officer or (ii) by the use of two-way electronic video and audio communication. All communications and proceedings shall be conducted in the same manner and the intake officer shall have the same powers as if the appearance were in person. Any documents filed may be transmitted by facsimile and the facsimile and any signatures thereon shall serve, for all purposes, as an original document. Any two-way electronic video and audio communication system used shall comply with the provisions of subsection B of § <a href="http://law.lis.virginia.gov/vacode/19.2-3.1/" target="_blank" rel="noreferrer">19.2-3.1</a>.</p><p>1977, c. 559; 1985, c. 260; 1996, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?961+ful+CHAP0755" target="_blank" rel="noreferrer">755</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?961+ful+CHAP0914" target="_blank" rel="noreferrer">914</a>; 1997, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?971+ful+CHAP0862" target="_blank" rel="noreferrer">862</a>; 2002, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?021+ful+CHAP0700" target="_blank" rel="noreferrer">700</a>.</p>',5),t=[o];function s(l,c,d,f,p,h){return a(),i("div",null,t)}const m=e(r,[["render",s]]);export{v as __pageData,m as default};
