import{_ as e,o as t,c as a,O as i}from"./chunks/framework.9482e208.js";const v=JSON.parse('{"title":"§ 16.1-256 Limitations as to issuance of warrants for juveniles; detention orders.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/16.1-256.md","filePath":"vacode/16.1-256.md"}'),o={name:"vacode/16.1-256.md"},r=i('<h1 id="§-16-1-256-limitations-as-to-issuance-of-warrants-for-juveniles-detention-orders" tabindex="-1">§ 16.1-256 Limitations as to issuance of warrants for juveniles; detention orders. <a class="header-anchor" href="#§-16-1-256-limitations-as-to-issuance-of-warrants-for-juveniles-detention-orders" aria-label="Permalink to &quot;§ 16.1-256 Limitations as to issuance of warrants for juveniles; detention orders.&quot;">​</a></h1><p>No warrant of arrest shall be issued for any juvenile by a magistrate, except as follows:</p><ol><li><p>As provided in § <a href="/vacode/16.1-260/">16.1-260</a> on appeal from a decision of an intake officer to refuse to authorize a petition based solely upon a finding that no probable cause exists; or</p></li><li><p>Upon a finding of probable cause to believe that the child is in need of services or is a delinquent, when (i) the court is not open and (ii) the judge and the intake officer of the juvenile and domestic relations district court are not reasonably available. For purposes of this section, the phrase &quot;not reasonably available&quot; means that neither the judge nor the intake officer of the juvenile and domestic relations district court could be reached after the appearance by the juvenile before a magistrate or that neither could arrive within one hour after he was contacted.</p></li></ol><p>When a magistrate is authorized to issue a warrant pursuant to subdivision 2, he may also issue a detention order, if the criteria for detention set forth in § <a href="/vacode/16.1-248.1/">16.1-248.1</a> have been satisfied.</p><p>Warrants issued pursuant to this section shall be delivered forthwith to the juvenile court.</p><p>Code 1950, § 16.1-195; 1956, c. 555; 1958, c. 344; 1973, c. 440; 1977, c. 559; 1979, c. 701; 1980, c. 234; 1981, c. 184; 1983, c. 349; 1986, c. 295; 1996, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?961+ful+CHAP0755" target="_blank" rel="noreferrer">755</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?961+ful+CHAP0914" target="_blank" rel="noreferrer">914</a>; 2021, Sp. Sess. I, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?212+ful+CHAP0030" target="_blank" rel="noreferrer">30</a>.</p>',6),n=[r];function s(c,l,d,f,h,u){return t(),a("div",null,n)}const _=e(o,[["render",s]]);export{v as __pageData,_ as default};
