import{_ as e,o as a,c as i,O as t}from"./chunks/framework.9482e208.js";const g=JSON.parse('{"title":"§ 16.1-278.11 Mental illness and intellectual disability.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/16.1-278.11.md","filePath":"vacode/16.1-278.11.md"}'),l={name:"vacode/16.1-278.11.md"},r=t('<h1 id="§-16-1-278-11-mental-illness-and-intellectual-disability" tabindex="-1">§ 16.1-278.11 Mental illness and intellectual disability. <a class="header-anchor" href="#§-16-1-278-11-mental-illness-and-intellectual-disability" aria-label="Permalink to &quot;§ 16.1-278.11 Mental illness and intellectual disability.&quot;">​</a></h1><p>In cases involving a person who is involuntarily admitted because of a mental illness or is judicially certified as eligible for admission to a training center for persons with intellectual disability, disposition shall be in accordance with the provisions of Chapter 8 (§ <a href="http://law.lis.virginia.gov/vacode/37.2-800/" target="_blank" rel="noreferrer">37.2-800</a> et seq.) of Title 37.2. A child shall not be committed pursuant to §§ <a href="http://law.lis.virginia.gov/vacode/16.1-278.2/" target="_blank" rel="noreferrer">16.1-278.2</a> through <a href="http://law.lis.virginia.gov/vacode/16.1-278.8/" target="_blank" rel="noreferrer">16.1-278.8</a> or the provisions of Title 37.2 to a maximum security unit within any state hospital where adults determined to be criminally insane reside.</p><p>1991, c. 534; 2005, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?051+ful+CHAP0716" target="_blank" rel="noreferrer">716</a>; 2012, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?121+ful+CHAP0476" target="_blank" rel="noreferrer">476</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?121+ful+CHAP0507" target="_blank" rel="noreferrer">507</a>.</p>',3),n=[r];function s(o,c,d,h,_,p){return a(),i("div",null,n)}const u=e(l,[["render",s]]);export{g as __pageData,u as default};
