import{_ as e,o as t,c as a,O as o}from"./chunks/framework.9482e208.js";const p=JSON.parse('{"title":"§ 16.1-278.7 Commitment to Department of Juvenile Justice.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/16.1-278.7.md","filePath":"vacode/16.1-278.7.md"}'),r={name:"vacode/16.1-278.7.md"},i=o('<h1 id="§-16-1-278-7-commitment-to-department-of-juvenile-justice" tabindex="-1">§ 16.1-278.7 Commitment to Department of Juvenile Justice. <a class="header-anchor" href="#§-16-1-278-7-commitment-to-department-of-juvenile-justice" aria-label="Permalink to &quot;§ 16.1-278.7 Commitment to Department of Juvenile Justice.&quot;">​</a></h1><p>Only a juvenile who is (i) adjudicated delinquent of an act enumerated in subsection B or C of § <a href="/vacode/16.1-269.1/">16.1-269.1</a> and is 11 years of age or older or (ii) 14 years of age or older may be committed to the Department of Juvenile Justice. In cases where a waiver of an investigation has been granted pursuant to subdivision A 14 or A 17 of § <a href="/vacode/16.1-278.8/">16.1-278.8</a>, at the time a court commits a child to the Department of Juvenile Justice the court shall order an investigation pursuant to § <a href="/vacode/16.1-273/">16.1-273</a> to be completed within 15 days. No juvenile court or circuit court shall order the commitment of any child jointly to the Department of Juvenile Justice and to a local board of social services or transfer the custody of a child jointly to a court service unit of a juvenile court and to a local board of social services. Any person sentenced and committed to an active term of incarceration in the Department of Corrections who is, at the time of such sentencing, in the custody of the Department of Juvenile Justice, upon pronouncement of sentence, shall be immediately transferred to the Department of Corrections.</p><p>1991, c. 534; 2000, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?001+ful+CHAP0954" target="_blank" rel="noreferrer">954</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?001+ful+CHAP0981" target="_blank" rel="noreferrer">981</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?001+ful+CHAP0988" target="_blank" rel="noreferrer">988</a>; 2007, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?071+ful+CHAP0510" target="_blank" rel="noreferrer">510</a>; 2014, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?141+ful+CHAP0020" target="_blank" rel="noreferrer">20</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?141+ful+CHAP0249" target="_blank" rel="noreferrer">249</a>; 2021, Sp. Sess. I, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?212+ful+CHAP0115" target="_blank" rel="noreferrer">115</a>.</p>',3),n=[i];function c(l,s,f,u,m,d){return t(),a("div",null,n)}const g=e(r,[["render",c]]);export{p as __pageData,g as default};
