import{_ as e,o as r,c as t,O as a}from"./chunks/framework.9482e208.js";const p=JSON.parse('{"title":"§ 16.1-278.8:01 Juveniles found delinquent of first drug offense; screening; assessment; drug tests; costs and fees; education or treatment programs.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/16.1-278.8:01.md","filePath":"vacode/16.1-278.8:01.md"}'),n={name:"vacode/16.1-278.8:01.md"},o=a('<h1 id="§-16-1-278-8-01-juveniles-found-delinquent-of-first-drug-offense-screening-assessment-drug-tests-costs-and-fees-education-or-treatment-programs" tabindex="-1">§ 16.1-278.8:01 Juveniles found delinquent of first drug offense; screening; assessment; drug tests; costs and fees; education or treatment programs. <a class="header-anchor" href="#§-16-1-278-8-01-juveniles-found-delinquent-of-first-drug-offense-screening-assessment-drug-tests-costs-and-fees-education-or-treatment-programs" aria-label="Permalink to &quot;§ 16.1-278.8:01 Juveniles found delinquent of first drug offense; screening; assessment; drug tests; costs and fees; education or treatment programs.&quot;">​</a></h1><p>Whenever any juvenile who has not previously been found delinquent of any offense under Chapter 11 (§ <a href="/vacode/4.1-1100/">4.1-1100</a> et seq.) of Title 4.1 or Article 1 (§ <a href="/vacode/18.2-247/">18.2-247</a> et seq.) of Chapter 7 of Title 18.2 or under any statute of the United States or of any state relating to narcotic drugs, marijuana, or stimulant, depressant or hallucinogenic drugs, or has not previously had a proceeding against him for a violation of such an offense dismissed as provided in § 4.1- 1120 or <a href="/vacode/18.2-251/">18.2-251</a>, is found delinquent of any offense concerning the use, in any manner, of drugs, controlled substances, narcotics, marijuana, noxious chemical substances and like substances, the juvenile court or the circuit court shall require such juvenile to undergo a substance abuse screening pursuant to § <a href="/vacode/16.1-273/">16.1-273</a> and to submit to such periodic substance abuse testing, to include alcohol testing, as may be directed by the court. Such testing shall be conducted by a court services unit of the Department of Juvenile Justice, or by a locally operated court services unit or by personnel of any program or agency approved by the Department. The cost of such testing ordered by the court shall be paid by the Commonwealth from funds appropriated to the Department for this purpose. The court shall also order the juvenile to undergo such treatment or education program for substance abuse, if available, as the court deems appropriate based upon consideration of the substance abuse assessment. The treatment or education shall be provided by a program licensed by the Department of Behavioral Health and Developmental Services or by a similar program available through a facility or program operated by or under contract to the Department of Juvenile Justice or a locally operated court services unit or a program funded through the Virginia Juvenile Community Crime Control Act (§ <a href="/vacode/16.1-309.2/">16.1-309.2</a> et seq.).</p><p>2000, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?001+ful+CHAP1020" target="_blank" rel="noreferrer">1020</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?001+ful+CHAP1041" target="_blank" rel="noreferrer">1041</a>; 2009, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?091+ful+CHAP0813" target="_blank" rel="noreferrer">813</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?091+ful+CHAP0840" target="_blank" rel="noreferrer">840</a>; 2011, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?111+ful+CHAP0384" target="_blank" rel="noreferrer">384</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?111+ful+CHAP0410" target="_blank" rel="noreferrer">410</a>; 2014, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?141+ful+CHAP0674" target="_blank" rel="noreferrer">674</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?141+ful+CHAP0719" target="_blank" rel="noreferrer">719</a>; 2021, Sp. Sess. I, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?212+ful+CHAP0550" target="_blank" rel="noreferrer">550</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?212+ful+CHAP0551" target="_blank" rel="noreferrer">551</a>.</p>',3),s=[o];function i(l,c,u,f,d,g){return r(),t("div",null,s)}const m=e(n,[["render",i]]);export{p as __pageData,m as default};
