import{_ as e,o as t,c as a,O as n}from"./chunks/framework.9482e208.js";const v=JSON.parse('{"title":"§ 16.1-293.1 Mental health services transition plan.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/16.1-293.1.md","filePath":"vacode/16.1-293.1.md"}'),r={name:"vacode/16.1-293.1.md"},i=n('<h1 id="§-16-1-293-1-mental-health-services-transition-plan" tabindex="-1">§ 16.1-293.1 Mental health services transition plan. <a class="header-anchor" href="#§-16-1-293-1-mental-health-services-transition-plan" aria-label="Permalink to &quot;§ 16.1-293.1 Mental health services transition plan.&quot;">​</a></h1><p>A. The Board of Juvenile Justice, after consultation with the Department of Behavioral Health and Developmental Services, shall promulgate regulations for the planning and provision of post-release services for persons committed to the Department of Juvenile Justice pursuant to subdivision A 14 of § <a href="http://law.lis.virginia.gov/vacode/16.1-278.8/" target="_blank" rel="noreferrer">16.1-278.8</a> or placed in a postdispositional detention program pursuant to subsection B of § <a href="http://law.lis.virginia.gov/vacode/16.1-284.1/" target="_blank" rel="noreferrer">16.1-284.1</a> and identified as having a recognized mental health, substance abuse, or other therapeutic treatment need. The plan shall be in writing and completed prior to the person&#39;s release. The purpose of the plan shall be to ensure continuity of necessary treatment and services.</p><p>B. The mental health services transition plan shall identify the mental health, substance abuse, or other therapeutic needs of the person being released. Appropriate treatment providers and other persons from state and local agencies or entities, as defined by the Board, shall participate in the development of the plan. Appropriate family members, caregivers, or other persons, as defined by the Board, shall be invited to participate in the development of the person&#39;s plan.</p><p>C. Prior to the person&#39;s release from incarceration, the identified agency or agencies responsible for the case management of the mental health services transition plan shall make the necessary referrals specified in the plan and assist the person in applying for insurance and other services identified in the plan, including completing and submitting applications that may only be submitted upon release.</p><p>2005, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?051+ful+CHAP0334" target="_blank" rel="noreferrer">334</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?051+ful+CHAP0405" target="_blank" rel="noreferrer">405</a>; 2009, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?091+ful+CHAP0813" target="_blank" rel="noreferrer">813</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?091+ful+CHAP0840" target="_blank" rel="noreferrer">840</a>.</p>',5),s=[i];function o(l,p,h,c,d,f){return t(),a("div",null,s)}const m=e(r,[["render",o]]);export{v as __pageData,m as default};
