import{_ as e,o as a,c as t,O as r}from"./chunks/framework.9482e208.js";const v=JSON.parse('{"title":"§ 16.1-309.4 Statewide plan for juvenile services.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/16.1-309.4.md","filePath":"vacode/16.1-309.4.md"}'),i={name:"vacode/16.1-309.4.md"},n=r('<h1 id="§-16-1-309-4-statewide-plan-for-juvenile-services" tabindex="-1">§ 16.1-309.4 Statewide plan for juvenile services. <a class="header-anchor" href="#§-16-1-309-4-statewide-plan-for-juvenile-services" aria-label="Permalink to &quot;§ 16.1-309.4 Statewide plan for juvenile services.&quot;">​</a></h1><p>It shall be the duty of the Department of Juvenile Justice to devise, develop and promulgate a statewide plan for the establishment and maintenance of a range of institutional and community-based, diversion, predispositional and postdispositional services to be reasonably accessible to each court. The Department shall be responsible for the collection and dissemination of the required court data necessary for the development of the plan. The plan shall utilize the information provided by local plans submitted under § <a href="http://law.lis.virginia.gov/vacode/16.1-309.3/" target="_blank" rel="noreferrer">16.1-309.3</a>. The plan shall be submitted to the Board on or before July 1 in odd-numbered years. The plan shall include a biennial forecast with appropriate annual updates as may be required of future juvenile correctional center and detention home needs.</p><p>1995, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?951+ful+CHAP0698" target="_blank" rel="noreferrer">698</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?951+ful+CHAP0840" target="_blank" rel="noreferrer">840</a>; 1996, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?961+ful+CHAP0671" target="_blank" rel="noreferrer">671</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?961+ful+CHAP0682" target="_blank" rel="noreferrer">682</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?961+ful+CHAP0755" target="_blank" rel="noreferrer">755</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?961+ful+CHAP0914" target="_blank" rel="noreferrer">914</a>.</p>',3),o=[n];function l(s,c,d,p,f,h){return a(),t("div",null,o)}const _=e(i,[["render",l]]);export{v as __pageData,_ as default};
