import{_ as e,o as i,c as r,O as t}from"./chunks/framework.9482e208.js";const v=JSON.parse('{"title":"§ 16.1-309 Penalty.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/16.1-309.md","filePath":"vacode/16.1-309.md"}'),o={name:"vacode/16.1-309.md"},a=t('<h1 id="§-16-1-309-penalty" tabindex="-1">§ 16.1-309 Penalty. <a class="header-anchor" href="#§-16-1-309-penalty" aria-label="Permalink to &quot;§ 16.1-309 Penalty.&quot;">​</a></h1><p>A. Except as provided in §§ <a href="http://law.lis.virginia.gov/vacode/16.1-299/" target="_blank" rel="noreferrer">16.1-299</a>, <a href="http://law.lis.virginia.gov/vacode/16.1-300/" target="_blank" rel="noreferrer">16.1-300</a>, <a href="http://law.lis.virginia.gov/vacode/16.1-301/" target="_blank" rel="noreferrer">16.1-301</a>, <a href="http://law.lis.virginia.gov/vacode/16.1-305/" target="_blank" rel="noreferrer">16.1-305</a> and <a href="http://law.lis.virginia.gov/vacode/16.1-307/" target="_blank" rel="noreferrer">16.1-307</a>, any person who (i) files a petition, (ii) receives a petition or has access to court records in an official capacity, (iii) participates in the investigation of allegations which form the basis of a petition, (iv) is interviewed concerning such allegations and whose information is derived solely from such interview or (v) is present during any court proceeding, who discloses or makes use of or knowingly permits the use of identifying information not otherwise available to the public concerning a juvenile who is suspected of being or is the subject of a proceeding within the jurisdiction of the juvenile court pursuant to subdivisions A 1 through 5 or subdivision A 7 of § <a href="http://law.lis.virginia.gov/vacode/16.1-241/" target="_blank" rel="noreferrer">16.1-241</a> or who is in the custody of the State Department of Juvenile Justice, which information is directly or indirectly derived from the records or files of a law-enforcement agency, court or the Department of Juvenile Justice or acquired in the course of official duties, is guilty of a Class 3 misdemeanor.</p><p>B. The provisions of this section shall not apply to any law-enforcement officer or school employee who discloses to school personnel identifying information concerning a juvenile who is suspected of committing or has committed a delinquent act that has met applicable criteria of § <a href="http://law.lis.virginia.gov/vacode/16.1-260/" target="_blank" rel="noreferrer">16.1-260</a> and is committed or alleged to have been committed on school property during a school-sponsored activity or on the way to or from such activity, if the disclosure is made solely for the purpose of enabling school personnel to take appropriate disciplinary action within the school setting against the juvenile. Further, the provisions of this section shall not apply to school personnel who disclose information obtained pursuant to §§ <a href="http://law.lis.virginia.gov/vacode/16.1-305.1/" target="_blank" rel="noreferrer">16.1-305.1</a> and <a href="http://law.lis.virginia.gov/vacode/22.1-288.2/" target="_blank" rel="noreferrer">22.1-288.2</a>, if the disclosure is made in compliance with those sections.</p><p>1977, c. 559; 1978, c. 626; 1979, c. 481; 1989, cc. 520, 733; 1993, cc. 645, 889; 1994, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?941+ful+CHAP0835" target="_blank" rel="noreferrer">835</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?941+ful+CHAP0913" target="_blank" rel="noreferrer">913</a>; 1996, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?961+ful+CHAP0755" target="_blank" rel="noreferrer">755</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?961+ful+CHAP0914" target="_blank" rel="noreferrer">914</a>; 2003, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?031+ful+CHAP0119" target="_blank" rel="noreferrer">119</a>; 2017, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?171+ful+CHAP0623" target="_blank" rel="noreferrer">623</a>.</p>',4),n=[a];function l(s,c,h,f,p,g){return i(),r("div",null,n)}const u=e(o,[["render",l]]);export{v as __pageData,u as default};
