import{_ as e,o as t,c as o,O as i}from"./chunks/framework.9482e208.js";const p=JSON.parse('{"title":"§ 16.1-322.4 Payments for children from other counties or cities.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/16.1-322.4.md","filePath":"vacode/16.1-322.4.md"}'),a={name:"vacode/16.1-322.4.md"},r=i('<h1 id="§-16-1-322-4-payments-for-children-from-other-counties-or-cities" tabindex="-1">§ 16.1-322.4 Payments for children from other counties or cities. <a class="header-anchor" href="#§-16-1-322-4-payments-for-children-from-other-counties-or-cities" aria-label="Permalink to &quot;§ 16.1-322.4 Payments for children from other counties or cities.&quot;">​</a></h1><p>Any locality or commission operating a juvenile secure detention facility may collect from any locality of this Commonwealth from which a child is placed in its facility a daily rate which does not exceed the sum total of the daily operating costs less any state aid for the purposes of construction and operation of such program. Daily cost shall be based on the cost of capital construction debt service and the cost of feeding, clothing, caring for, and furnishing medicine and medical attention for such child as may be agreed upon by the governmental units involved.</p><p>1983, c. 358; 1995, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?951+ful+CHAP0698" target="_blank" rel="noreferrer">698</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?951+ful+CHAP0840" target="_blank" rel="noreferrer">840</a>; 1998, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?981+ful+CHAP0856" target="_blank" rel="noreferrer">856</a>.</p>',3),n=[r];function c(s,l,h,d,f,m){return t(),o("div",null,n)}const u=e(a,[["render",c]]);export{p as __pageData,u as default};
