import{_ as e,o as i,c as t,O as r}from"./chunks/framework.9482e208.js";const p=JSON.parse('{"title":"§ 16.1-333.1 Written findings necessary to order that minor is emancipated on the basis of intent to marry.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/16.1-333.1.md","filePath":"vacode/16.1-333.1.md"}'),n={name:"vacode/16.1-333.1.md"},a=r('<h1 id="§-16-1-333-1-written-findings-necessary-to-order-that-minor-is-emancipated-on-the-basis-of-intent-to-marry" tabindex="-1">§ 16.1-333.1 Written findings necessary to order that minor is emancipated on the basis of intent to marry. <a class="header-anchor" href="#§-16-1-333-1-written-findings-necessary-to-order-that-minor-is-emancipated-on-the-basis-of-intent-to-marry" aria-label="Permalink to &quot;§ 16.1-333.1 Written findings necessary to order that minor is emancipated on the basis of intent to marry.&quot;">​</a></h1><p>The court may enter an order declaring such a minor who desires to get married emancipated if, after a hearing where both individuals intending to marry are present, the court makes written findings that:</p><ol><li><p>It is the minor&#39;s own will that the minor enter into marriage, and the minor is not being compelled against the minor&#39;s will by force, threats, persuasions, menace, or duress;</p></li><li><p>The individuals to be married are mature enough to make such a decision to marry;</p></li><li><p>The marriage will not endanger the safety of the minor. In making this finding, the court shall consider (i) the age difference between the parties intending to be married; (ii) whether either individual to be married has a criminal record containing any conviction of an act of violence, as defined in § <a href="http://law.lis.virginia.gov/vacode/19.2-297.1/" target="_blank" rel="noreferrer">19.2-297.1</a>, or any conviction of a barrier crime, as defined in § <a href="http://law.lis.virginia.gov/vacode/19.2-392.02/" target="_blank" rel="noreferrer">19.2-392.02</a>; and (iii) any history of violence between the parties to be married; and</p></li><li><p>It is in the best interests of the minor petitioning for an order of emancipation that such order be entered. Neither a past or current pregnancy of either individual to be married or between the individuals to be married nor the wishes of the parents or legal guardians of the minor desiring to be married shall be sufficient evidence to establish that the best interests of the minor would be served by entering the order of emancipation.</p></li></ol><p>2016, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?161+ful+CHAP0457" target="_blank" rel="noreferrer">457</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?161+ful+CHAP0543" target="_blank" rel="noreferrer">543</a>; 2017, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?171+ful+CHAP0809" target="_blank" rel="noreferrer">809</a>.</p>',4),o=[a];function s(d,h,c,l,m,f){return i(),t("div",null,o)}const b=e(n,[["render",s]]);export{p as __pageData,b as default};
