import{_ as r,o as i,c as n,z as e,a as t}from"./chunks/framework.9482e208.js";const _=JSON.parse('{"title":"§ 16.1-340.4 Involuntary commitment; preadmission screening report.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/16.1-340.4.md","filePath":"vacode/16.1-340.4.md"}'),a={name:"vacode/16.1-340.4.md"},o=e("h1",{id:"§-16-1-340-4-involuntary-commitment-preadmission-screening-report",tabindex:"-1"},[t("§ 16.1-340.4 Involuntary commitment; preadmission screening report. "),e("a",{class:"header-anchor",href:"#§-16-1-340-4-involuntary-commitment-preadmission-screening-report","aria-label":'Permalink to "§ 16.1-340.4 Involuntary commitment; preadmission screening report."'},"​")],-1),s=e("p",null,"The juvenile and domestic relations district court shall require a preadmission screening report from the community services board that serves the area where the minor resides or, if impractical, where the minor is located. The report shall be prepared by an employee or designee of the community services board. The report shall be admitted as evidence of the facts stated therein and shall state (i) whether the minor has mental illness and whether, because of mental illness, the minor (a) presents a serious danger to himself or others to the extent that severe or irremediable injury is likely to result, as evidenced by recent acts or threats, or (b) is experiencing a serious deterioration of his ability to care for himself in a developmentally age-appropriate manner, as evidenced by delusionary thinking or by a significant impairment of functioning in hydration, nutrition, self-protection, or self-control; (ii) whether the minor is in need of compulsory treatment for a mental illness and is reasonably likely to benefit from the proposed treatment; (iii) whether inpatient treatment is the least restrictive alternative that meets the minor's needs; and (iv) the recommendations for the minor's placement, care, and treatment including, where appropriate, recommendations for mandatory outpatient treatment. The board shall provide the preadmission screening report to the court prior to the hearing, and the report shall be admitted into evidence and made part of the record of the case.",-1),l=e("p",null,[t("2010, cc. "),e("a",{href:"http://lis.virginia.gov/cgi-bin/legp604.exe?101+ful+CHAP0778",target:"_blank",rel:"noreferrer"},"778"),t(", "),e("a",{href:"http://lis.virginia.gov/cgi-bin/legp604.exe?101+ful+CHAP0825",target:"_blank",rel:"noreferrer"},"825"),t(".")],-1),c=[o,s,l];function m(d,h,p,f,u,v){return i(),n("div",null,c)}const b=r(a,[["render",m]]);export{_ as __pageData,b as default};
