import{_ as e,o,c as t,O as a}from"./chunks/framework.9482e208.js";const _=JSON.parse('{"title":"§ 16.1-69.24 Contempt of court.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/16.1-69.24.md","filePath":"vacode/16.1-69.24.md"}'),r={name:"vacode/16.1-69.24.md"},n=a('<h1 id="§-16-1-69-24-contempt-of-court" tabindex="-1">§ 16.1-69.24 Contempt of court. <a class="header-anchor" href="#§-16-1-69-24-contempt-of-court" aria-label="Permalink to &quot;§ 16.1-69.24 Contempt of court.&quot;">​</a></h1><p>A. A judge of a district court shall have the same powers and jurisdiction as a judge of a circuit court to punish summarily for contempt, but in no case shall the fine exceed $250 and imprisonment exceed 10 days for the same contempt. From any such fine or sentence, there shall be an appeal of right within the period prescribed in this title and to the court or courts designated therein for appeals in other cases, and the proceedings on such appeal shall conform in all respects to the provisions of §§ <a href="/vacode/18.2-456/">18.2-456</a> through <a href="/vacode/18.2-459/">18.2-459</a>.</p><p>B. Any person charged with a felony offense, misdemeanor offense, or released on a summons pursuant to § <a href="/vacode/19.2-73/">19.2-73</a> or <a href="/vacode/19.2-74/">19.2-74</a> who fails to appear before any court or judicial officer as required shall not be punished for contempt under this provision but may be punished for such contempt under subdivision A 6 of § <a href="/vacode/18.2-456/">18.2-456</a>.</p><p>1972, c. 708; 1973, c. 546; 2000, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?001+ful+CHAP0164" target="_blank" rel="noreferrer">164</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?001+ful+CHAP0185" target="_blank" rel="noreferrer">185</a>; 2019, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?191+ful+CHAP0708" target="_blank" rel="noreferrer">708</a>.</p>',4),i=[n];function s(c,h,p,d,f,l){return o(),t("div",null,i)}const m=e(r,[["render",s]]);export{_ as __pageData,m as default};
