import{_ as t,o as e,c as o,O as i}from"./chunks/framework.9482e208.js";const _=JSON.parse('{"title":"§ 16.1-69.5 Meaning of certain terms.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/16.1-69.5.md","filePath":"vacode/16.1-69.5.md"}'),s={name:"vacode/16.1-69.5.md"},r=i('<h1 id="§-16-1-69-5-meaning-of-certain-terms" tabindex="-1">§ 16.1-69.5 Meaning of certain terms. <a class="header-anchor" href="#§-16-1-69-5-meaning-of-certain-terms" aria-label="Permalink to &quot;§ 16.1-69.5 Meaning of certain terms.&quot;">​</a></h1><p>Unless the context should otherwise require, the terms set out in this section shall be construed as follows:</p><p>(a) &quot;Courts not of record&quot; shall mean all courts in the Commonwealth below the jurisdictional level of the circuit courts including general district courts and juvenile and domestic relations district courts;</p><p>(b) &quot;General district courts&quot; shall mean all courts not of record, except juvenile and domestic relations district courts in counties and cities heretofore designated as county and municipal courts;</p><p>(c) &quot;Juvenile and domestic relations district courts&quot; shall mean all courts in counties and cities heretofore designated as juvenile and domestic relations courts or regional juvenile and domestic relations courts;</p><p>(d) &quot;District courts&quot; shall mean general district courts and juvenile and domestic relations district courts;</p><p>(e) &quot;County courts&quot; and &quot;municipal courts&quot; shall be deemed to refer to general district courts;</p><p>(f) &quot;Juvenile and domestic relations courts&quot; and &quot;regional juvenile and domestic relations courts&quot; shall be deemed to refer to juvenile and domestic relations district courts; and</p><p>(g) &quot;Chief judge&quot; shall mean that judge so designated for a term to assume primary administrative responsibility for the general district courts or the juvenile and domestic relations district courts in the district served by such judge.</p><p>1972, c. 708; 1973, c. 546; 1975, c. 334.</p>',10),a=[r];function n(c,d,u,l,m,h){return e(),o("div",null,a)}const f=t(s,[["render",n]]);export{_ as __pageData,f as default};
