import{_ as e,o,c as a,z as t,a as n}from"./chunks/framework.9482e208.js";const p=JSON.parse('{"title":"§ 16.1-81 Actions brought by motion for judgment.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/16.1-81.md","filePath":"vacode/16.1-81.md"}'),i={name:"vacode/16.1-81.md"},s=t("h1",{id:"§-16-1-81-actions-brought-by-motion-for-judgment",tabindex:"-1"},[n("§ 16.1-81 Actions brought by motion for judgment. "),t("a",{class:"header-anchor",href:"#§-16-1-81-actions-brought-by-motion-for-judgment","aria-label":'Permalink to "§ 16.1-81 Actions brought by motion for judgment."'},"​")],-1),r=t("p",null,"A civil action in a general district court may be brought by motion for judgment. Such motion shall be in writing, signed by the plaintiff or his attorney, and shall contain a caption setting forth the name of the court and the title of the action, which shall include the names of all parties and the address of each defendant. It shall state the facts on which the plaintiff relies, and shall be sufficient if it clearly informs the defendant or defendants of the true nature of the claim asserted. The motion shall notify the defendant or defendants of the day on which such motion shall be made, which day shall not be more than sixty days from the date of service of the motion.",-1),h=t("p",null,"1956, c. 555; 1990, c. 762.",-1),c=[s,r,h];function d(l,f,m,u,_,b){return o(),a("div",null,c)}const y=e(i,[["render",d]]);export{p as __pageData,y as default};
