import{_ as a,o as n,c as o,z as e,a as t}from"./chunks/framework.9482e208.js";const y=JSON.parse('{"title":"§ 16.1-88.01 Counterclaims.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/16.1-88.01.md","filePath":"vacode/16.1-88.01.md"}'),r={name:"vacode/16.1-88.01.md"},i=e("h1",{id:"§-16-1-88-01-counterclaims",tabindex:"-1"},[t("§ 16.1-88.01 Counterclaims. "),e("a",{class:"header-anchor",href:"#§-16-1-88-01-counterclaims","aria-label":'Permalink to "§ 16.1-88.01 Counterclaims."'},"​")],-1),s=e("p",null,"In any proceeding before any general district court a defendant may, at his option, at any time before trial, plead in writing as a counterclaim, any cause of action at law for a money judgment in personam, or any matter which would entitle him to relief in equity in the nature of damages, that he has against the plaintiff or all plaintiffs jointly, whether or not it grows out of any transaction mentioned in the warrant or notice of motion for judgment, whether or not it is for liquidated damages, whether or not it is in tort or contract, and whether or not the amount demanded exceeds the amount claimed by the plaintiff in the warrant or notice of motion for judgment; however, no such counterclaim shall be filed or heard when the amount claimed therein exceeds the amount within the jurisdiction of such court.",-1),c=e("p",null,"Upon the request of either party, bills of particulars and grounds of defense may be ordered to ensure a fair trial on the merits of the issue presented. The court may, in its discretion, hear the counterclaim together with the original case, or may order and hold a separate hearing of any cause of action asserted in a counterclaim. In either event, the court shall render such final judgment on the whole case as the law and the evidence require.",-1),h=e("p",null,[t("Code 1950, § 8-239.1; 1954, c. 608; 1977, c. 624; 1998, cc. "),e("a",{href:"http://lis.virginia.gov/cgi-bin/legp604.exe?981+ful+CHAP0482",target:"_blank",rel:"noreferrer"},"482"),t(", "),e("a",{href:"http://lis.virginia.gov/cgi-bin/legp604.exe?981+ful+CHAP0495",target:"_blank",rel:"noreferrer"},"495"),t(".")],-1),l=[i,s,c,h];function d(u,f,m,p,g,_){return n(),o("div",null,l)}const b=a(r,[["render",d]]);export{y as __pageData,b as default};
