import{_ as a,o as t,c as o,z as e,a as s}from"./chunks/framework.9482e208.js";const y=JSON.parse('{"title":"§ 16.1-93 Principles applicable to trial of cases.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/16.1-93.md","filePath":"vacode/16.1-93.md"}'),r={name:"vacode/16.1-93.md"},i=e("h1",{id:"§-16-1-93-principles-applicable-to-trial-of-cases",tabindex:"-1"},[s("§ 16.1-93 Principles applicable to trial of cases. "),e("a",{class:"header-anchor",href:"#§-16-1-93-principles-applicable-to-trial-of-cases","aria-label":'Permalink to "§ 16.1-93 Principles applicable to trial of cases."'},"​")],-1),c=e("p",null,"Every action or other proceeding in a court not of record shall be tried according to the principles of law and equity, and when the same conflict the principles of equity shall prevail. No warrant, motion or other pleading shall be dismissed by reason of a mere defect, irregularity or omission in the proceedings or in the form of the pleadings when the same may be corrected by an order of the court. The court may direct such proceedings and enter such orders as may be necessary to correct any such defects, irregularities and omissions, and to bring about a trial of the merits of the controversy and promote substantial justice to all parties. The court may make such provisions as to costs and continuances as may be just.",-1),n=e("p",null,"1956, c. 555.",-1),l=[i,c,n];function d(p,h,f,m,u,_){return t(),o("div",null,l)}const g=a(r,[["render",d]]);export{y as __pageData,g as default};
