import{_ as o,o as n,c as r,z as e,a as t}from"./chunks/framework.9482e208.js";const _=JSON.parse('{"title":"§ 16.1-94 Judgment to be noted on papers; formal orders may be entered.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/16.1-94.md","filePath":"vacode/16.1-94.md"}'),a={name:"vacode/16.1-94.md"},s=e("h1",{id:"§-16-1-94-judgment-to-be-noted-on-papers-formal-orders-may-be-entered",tabindex:"-1"},[t("§ 16.1-94 Judgment to be noted on papers; formal orders may be entered. "),e("a",{class:"header-anchor",href:"#§-16-1-94-judgment-to-be-noted-on-papers-formal-orders-may-be-entered","aria-label":'Permalink to "§ 16.1-94 Judgment to be noted on papers; formal orders may be entered."'},"​")],-1),d=e("p",null,"Whenever a judgment is rendered in a court not of record the judgment shall be entered on the warrant, motion for judgment, counterclaim, cross-claim or other pleading and signed by the judge, or the signature of the judge may be affixed by a facsimile stamp, in which event the judge shall initial a notation of the judgment made on the warrant or other paper. If the action is on a note, bond or other written obligation, the date and amount of the judgment rendered shall be noted thereon, to which notation the judge or clerk shall affix his name or his initials. Nothing in this section shall be construed to prevent the judge from entering a formal order in any case in which he deems such order to be appropriate, including but not limited to settlement and installment orders endorsed by counsel, or to affect the validity of any formal order so entered. If such action is on a lease for the recovery of rent or possession of property this section shall not operate to require marking of such lease unless the judge deems such marking necessary.",-1),i=e("p",null,[t("1956, c. 555; 1962, c. 361; 2004, c. "),e("a",{href:"http://lis.virginia.gov/cgi-bin/legp604.exe?041+ful+CHAP0341",target:"_blank",rel:"noreferrer"},"341"),t(".")],-1),l=[s,d,i];functi