import{_ as e,o,c as t,O as a}from"./chunks/framework.9482e208.js";const p=JSON.parse('{"title":"§ 17.1-222.1 Collection of fees, etc., by court clerks in lieu of money.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/17.1-222.1.md","filePath":"vacode/17.1-222.1.md"}'),r={name:"vacode/17.1-222.1.md"},c=a('<h1 id="§-17-1-222-1-collection-of-fees-etc-by-court-clerks-in-lieu-of-money" tabindex="-1">§ 17.1-222.1 Collection of fees, etc., by court clerks in lieu of money. <a class="header-anchor" href="#§-17-1-222-1-collection-of-fees-etc-by-court-clerks-in-lieu-of-money" aria-label="Permalink to &quot;§ 17.1-222.1 Collection of fees, etc., by court clerks in lieu of money.&quot;">​</a></h1><p>The clerks of the several courts of the Commonwealth may, in lieu of money, collect or secure all fees, and fines and penalties collected for offenses committed against the Commonwealth or against any county, city or town, and for payment of spousal or child support, by any means provided in § <a href="http://law.lis.virginia.gov/vacode/2.2-614.1/" target="_blank" rel="noreferrer">2.2-614.1</a>. Such clerks shall not by virtue of acceptance of any such payment be held to be guarantors of the payment thereof, but they shall exercise ordinary care in such acceptance.</p><p>1977, c. 139, § 14.1-3.1; 1978, c. 117; 1998, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?981+ful+CHAP0872" target="_blank" rel="noreferrer">872</a>; 2002, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?021+ful+CHAP0719" target="_blank" rel="noreferrer">719</a>.</p>',3),n=[c];function l(i,s,f,h,_,u){return o(),t("div",null,n)}const m=e(r,[["render",l]]);export{p as __pageData,m as default};
