import{_ as e,o as r,c as o,O as a}from"./chunks/framework.9482e208.js";const _=JSON.parse('{"title":"§ 17.1-239 Character of books used for recording; standards for microfilm, etc.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/17.1-239.md","filePath":"vacode/17.1-239.md"}'),t={name:"vacode/17.1-239.md"},s=a('<h1 id="§-17-1-239-character-of-books-used-for-recording-standards-for-microfilm-etc" tabindex="-1">§ 17.1-239 Character of books used for recording; standards for microfilm, etc. <a class="header-anchor" href="#§-17-1-239-character-of-books-used-for-recording-standards-for-microfilm-etc" aria-label="Permalink to &quot;§ 17.1-239 Character of books used for recording; standards for microfilm, etc.&quot;">​</a></h1><p>All books used in the clerk&#39;s office for the permanent recordation of deeds, wills and other instruments shall either be made of high quality record paper, strictly number one rag stock, or permanent-durable, acid-free paper composed of strong, well-purified chemical wood fibers as prescribed by regulations pursuant to subdivision A 2 of § <a href="http://law.lis.virginia.gov/vacode/42.1-82/" target="_blank" rel="noreferrer">42.1-82</a>, or, if microfilm, microfiche, or microphotographic process is used in the clerk&#39;s office, the deeds, wills or other instruments shall be processed in accordance with standards established pursuant to § <a href="http://law.lis.virginia.gov/vacode/42.1-82/" target="_blank" rel="noreferrer">42.1-82</a>. All books are to be substantially bound, with a durable cloth or plastic cover over all.</p><p>Code 1919, § 3399, § 17-70; 1934, p. 174; 1972, c. 501; 1975, c. 58; 1983, c. 293; 1988, c. 35; 1998, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?981+ful+CHAP0872" target="_blank" rel="noreferrer">872</a>.</p>',3),i=[s];function c(d,n,l,f,h,p){return r(),o("div",null,i)}const u=e(t,[["render",c]]);export{_ as __pageData,u as default};
