import{_ as e,o as i,c as a,O as n}from"./chunks/framework.9482e208.js";const g=JSON.parse('{"title":"§ 17.1-275.1 Fixed felony fee.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/17.1-275.1.md","filePath":"vacode/17.1-275.1.md"}'),r={name:"vacode/17.1-275.1.md"},o=n('<h1 id="§-17-1-275-1-fixed-felony-fee" tabindex="-1">§ 17.1-275.1 Fixed felony fee. <a class="header-anchor" href="#§-17-1-275-1-fixed-felony-fee" aria-label="Permalink to &quot;§ 17.1-275.1 Fixed felony fee.&quot;">​</a></h1><p>Upon conviction of any and each felony charge or upon a deferred disposition of proceedings in circuit court in the case of any and each felony disposition deferred pursuant to the terms and conditions of § <a href="/vacode/16.1-278.8/">16.1-278.8</a>, <a href="/vacode/16.1-278.9/">16.1-278.9</a>, <a href="/vacode/18.2-61/">18.2-61</a>, <a href="/vacode/18.2-67.1/">18.2-67.1</a>, <a href="/vacode/18.2-251/">18.2-251</a>, <a href="/vacode/19.2-298.02/">19.2-298.02</a>, or <a href="/vacode/19.2-303.6/">19.2-303.6</a>, there shall be assessed as court costs a fee of $375, to be known as the fixed felony fee.</p><p>The amount collected, in whole or in part, for the fixed felony fee shall be apportioned, as provided by law, to the following funds in the fractional amounts designated:</p><ol><li><p>Sentencing/supervision fee (General Fund) (.4705067);</p></li><li><p>Forensic science fund (.1033333);</p></li><li><p>Court reporter fund (.0887200);</p></li><li><p>Witness expenses/expert witness fund (.0053333);</p></li><li><p>Virginia Crime Victim-Witness Fund (.0080000);</p></li><li><p>Intensified Drug Enforcement Jurisdiction Fund (.0106667);</p></li><li><p>Criminal Injuries Compensation Fund (.0800000);</p></li><li><p>Commonwealth&#39;s attorney fund (state share) (.0533333);</p></li><li><p>Commonwealth&#39;s attorney fund (local share) (.0533333);</p></li><li><p>Regional Criminal Justice Academy Training Fund (.0026667);</p></li><li><p>Warrant fee (.0320000);</p></li><li><p>Courthouse construction/maintenance fund (.0053333); and</p></li><li><p>Clerk of the circuit court (.0867733).</p></li></ol><p>1999, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?991+ful+CHAP0009" target="_blank" rel="noreferrer">9</a>; 2002, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?021+ful+CHAP0831" target="_blank" rel="noreferrer">831</a>; 2003, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?031+ful+CHAP1039" target="_blank" rel="noreferrer">1039</a>; 2005, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?051+ful+CHAP0631" target="_blank" rel="noreferrer">631</a>; 2011, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?111+ful+CHAP0565" target="_blank" rel="noreferrer">565</a>; 2020, Sp. Sess. I, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?202+ful+CHAP0021" target="_blank" rel="noreferrer">21</a>.</p>',5),t=[o];function l(s,f,c,p,d,h){return i(),a("div",null,t)}const _=e(r,[["render",l]]);export{g as __pageData,_ as default};
