import{_ as e,o as a,c as t,O as n}from"./chunks/framework.9482e208.js";const v=JSON.parse('{"title":"§ 17.1-293.1 (For contingent effective date see cc. 524 and 542) Online case information system; exceptions.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/17.1-293.1.md","filePath":"vacode/17.1-293.1.md"}'),i={name:"vacode/17.1-293.1.md"},r=n('<h1 id="§-17-1-293-1-for-contingent-effective-date-see-cc-524-and-542-online-case-information-system-exceptions" tabindex="-1">§ 17.1-293.1 (For contingent effective date see cc. 524 and 542) Online case information system; exceptions. <a class="header-anchor" href="#§-17-1-293-1-for-contingent-effective-date-see-cc-524-and-542-online-case-information-system-exceptions" aria-label="Permalink to &quot;§ 17.1-293.1 (For contingent effective date see cc. 524 and 542) Online case information system; exceptions.&quot;">​</a></h1><p>A. The Executive Secretary shall make available a publicly viewable online case information system of certain nonconfidential information entered into the case management system for criminal cases in the circuit courts participating in the Executive Secretary&#39;s case management system and in the general district courts. Such system shall be searchable by defendant name across all participating courts, and search results shall be viewable free of charge.</p><p>B. Upon entry of a sealing order pursuant to § <a href="/vacode/19.2-392.7/">19.2-392.7</a>, <a href="/vacode/19.2-392.8/">19.2-392.8</a>, <a href="/vacode/19.2-392.9/">19.2-392.9</a>, <a href="/vacode/19.2-392.11/">19.2-392.11</a>, or <a href="/vacode/19.2-392.12/">19.2-392.12</a>, the Executive Secretary shall not make any offense that was ordered to be sealed available for online public viewing in an appellate court, circuit court, or district court case management system maintained by the Executive Secretary.</p><p>C. Upon entry of a sealing order pursuant to § <a href="/vacode/19.2-392.7/">19.2-392.7</a>, <a href="/vacode/19.2-392.8/">19.2-392.8</a>, <a href="/vacode/19.2-392.9/">19.2-392.9</a>, <a href="/vacode/19.2-392.11/">19.2-392.11</a>, or <a href="/vacode/19.2-392.12/">19.2-392.12</a>, any circuit court clerk who maintains a viewable online case management or case information system shall not make any offense that was ordered to be sealed available for online public viewing.</p><p>2018, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?181+ful+CHAP0127" target="_blank" rel="noreferrer">127</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?181+ful+CHAP0584" target="_blank" rel="noreferrer">584</a>; 2021, Sp. Sess. I, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?212+ful+CHAP0524" target="_blank" rel="noreferrer">524</a>.</p>',5),o=[r];function c(s,l,f,d,h,m){return a(),t("div",null,o)}const g=e(i,[["render",c]]);export{v as __pageData,g as default};
