import{_ as e,o as t,c as o,O as i}from"./chunks/framework.9482e208.js";const p=JSON.parse('{"title":"§ 17.1-300 Composition of Court; quorum; Chief Justice.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/17.1-300.md","filePath":"vacode/17.1-300.md"}'),r={name:"vacode/17.1-300.md"},a=i('<h1 id="§-17-1-300-composition-of-court-quorum-chief-justice" tabindex="-1">§ 17.1-300 Composition of Court; quorum; Chief Justice. <a class="header-anchor" href="#§-17-1-300-composition-of-court-quorum-chief-justice" aria-label="Permalink to &quot;§ 17.1-300 Composition of Court; quorum; Chief Justice.&quot;">​</a></h1><p>The Supreme Court shall consist of seven justices, any four of whom convened shall constitute a quorum. The Chief Justice shall be elected by majority vote of the justices of the Supreme Court to serve a term of four years. An eligible justice may decline to serve as Chief Justice, or a Chief Justice may resign as such, without thereby relinquishing his membership on the Court as a justice thereof.</p><p>Code 1919, § 5862, § 17-93; 1938, p. 133; 1971, Ex. Sess., c. 51; 1998, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?981+ful+CHAP0872" target="_blank" rel="noreferrer">872</a>; 2002, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?021+ful+CHAP0043" target="_blank" rel="noreferrer">43</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?021+ful+CHAP0552" target="_blank" rel="noreferrer">552</a>.</p>',3),s=[a];function c(n,u,h,f,l,_){return t(),o("div",null,s)}const d=e(r,[["render",c]]);export{p as __pageData,d as default};
