import{_ as a,o as s,c as i,z as e,a as t}from"./chunks/framework.9482e208.js";const b=JSON.parse('{"title":"§ 17.1-306 What may be tried at special session; effect of decisions.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/17.1-306.md","filePath":"vacode/17.1-306.md"}'),o={name:"vacode/17.1-306.md"},r=e("h1",{id:"§-17-1-306-what-may-be-tried-at-special-session-effect-of-decisions",tabindex:"-1"},[t("§ 17.1-306 What may be tried at special session; effect of decisions. "),e("a",{class:"header-anchor",href:"#§-17-1-306-what-may-be-tried-at-special-session-effect-of-decisions","aria-label":'Permalink to "§ 17.1-306 What may be tried at special session; effect of decisions."'},"​")],-1),n=e("p",null,"At any such special session, the Supreme Court, by consent of the parties or their counsel, may hear and determine any cause then ready for a hearing, or, without such consent, upon twenty days' previous notice in writing, given by a party desiring a hearing to the adverse party, of his intention to insist on the same. The Court, at such special session, shall, after notice to the parties or their counsel from the clerk of the Court, hear any cause which, in its opinion, the public interest requires to be heard and determined. Any judgment, decree or order entered or made at such special session shall have the same effect and may be reviewed and reheard in like manner and subject to the same rules as a judgment, decree or order entered or made at a regular session.",-1),c=e("p",null,[t("Code 1919, § 5886, § 17-102; 1971, Ex. Sess., c. 51; 1998, c. "),e("a",{href:"http://lis.virginia.gov/cgi-bin/legp604.exe?981+ful+CHAP0872",target:"_blank",rel:"noreferrer"},"872"),t(".")],-1),d=[r,n,c];function h(l,f,p,u,m,_){return s(),i("div",null,d)}const g=a(o,[["render",h]]);export{b as __pageData,g as default};
