import{_ as e,o as i,c as r,O as a}from"./chunks/framework.9482e208.js";const m=JSON.parse(`{"title":"§ 17.1-405 Appellate jurisdiction — Administrative agency, Virginia Workers' Compensation Commission, and civil matter appeals.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/17.1-405.md","filePath":"vacode/17.1-405.md"}`),o={name:"vacode/17.1-405.md"},t=a('<h1 id="§-17-1-405-appellate-jurisdiction-—-administrative-agency-virginia-workers-compensation-commission-and-civil-matter-appeals" tabindex="-1">§ 17.1-405 Appellate jurisdiction — Administrative agency, Virginia Workers&#39; Compensation Commission, and civil matter appeals. <a class="header-anchor" href="#§-17-1-405-appellate-jurisdiction-—-administrative-agency-virginia-workers-compensation-commission-and-civil-matter-appeals" aria-label="Permalink to &quot;§ 17.1-405 Appellate jurisdiction — Administrative agency, Virginia Workers&#39; Compensation Commission, and civil matter appeals.&quot;">​</a></h1><p>Unless otherwise provided by law, any aggrieved party may appeal to the Court of Appeals from:</p><ol><li><p>Any final decision of a circuit court on appeal from (i) a decision of an administrative agency, or (ii) a grievance hearing decision issued pursuant to § <a href="/vacode/2.2-3005/">2.2-3005</a>;</p></li><li><p>Any final decision of the Virginia Workers&#39; Compensation Commission;</p></li><li><p>Except as provided in subsection B of § <a href="/vacode/17.1-406/">17.1-406</a>, any final judgment, order, or decree of a circuit court in a civil matter;</p></li><li><p>Any interlocutory decree or order pursuant to § <a href="/vacode/8.01-267.8/">8.01-267.8</a>;</p></li><li><p>Any interlocutory decree or order involving an equitable claim in which the decree or order (i) requires money to be paid or the possession or title of property to be changed or (ii) adjudicates the principles of a cause; or</p></li><li><p>Any final judgment, order, or decree of a circuit court (i) involving an application for a concealed weapons permit pursuant to Article 6.1 (§ <a href="/vacode/18.2-307.1/">18.2-307.1</a> et seq.) of Chapter 7 of Title 18.2, (ii) involving involuntary treatment of prisoners pursuant to § <a href="/vacode/53.1-40.1/">53.1-40.1</a> or <a href="/vacode/53.1-133.04/">53.1-133.04</a>, or (iii) for declaratory or injunctive relief under § <a href="/vacode/57-2.02/">57-2.02</a>.</p></li></ol><p>1983, c. 413, § 17-116.05; 1984, c. 701; 1985, c. 283; 1990, c. 897; 1998, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?981+ful+CHAP0872" target="_blank" rel="noreferrer">872</a>; 2000, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?001+ful+CHAP0830" target="_blank" rel="noreferrer">830</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?001+ful+CHAP0947" target="_blank" rel="noreferrer">947</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?001+ful+CHAP1006" target="_blank" rel="noreferrer">1006</a>; 2001, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?011+ful+CHAP0393" target="_blank" rel="noreferrer">393</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?011+ful+CHAP0420" target="_blank" rel="noreferrer">420</a>; 2021, Sp. Sess. I, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?211+ful+CHAP0489" target="_blank" rel="noreferrer">489</a>; 2022, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?221+ful+CHAP0307" target="_blank" rel="noreferrer">307</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?221+ful+CHAP0714" target="_blank" rel="noreferrer">714</a>.</p>',4),n=[t];function l(c,s,p,d,g,f){return i(),r("div",null,n)}const u=e(o,[["render",l]]);export{m as __pageData,u as default};
