import{_ as e,o as a,c as r,O as i}from"./chunks/framework.9482e208.js";const g=JSON.parse('{"title":"§ 17.1-406 Appeals in criminal matters; cases over which Court of Appeals does not have jurisdiction.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/17.1-406.md","filePath":"vacode/17.1-406.md"}'),o={name:"vacode/17.1-406.md"},t=i('<h1 id="§-17-1-406-appeals-in-criminal-matters-cases-over-which-court-of-appeals-does-not-have-jurisdiction" tabindex="-1">§ 17.1-406 Appeals in criminal matters; cases over which Court of Appeals does not have jurisdiction. <a class="header-anchor" href="#§-17-1-406-appeals-in-criminal-matters-cases-over-which-court-of-appeals-does-not-have-jurisdiction" aria-label="Permalink to &quot;§ 17.1-406 Appeals in criminal matters; cases over which Court of Appeals does not have jurisdiction.&quot;">​</a></h1><p>A. Any aggrieved party may appeal to the Court of Appeals from any final conviction in a circuit court of a traffic infraction or a crime. The Commonwealth or any county, city, or town may petition the Court of Appeals for an appeal pursuant to this subsection in any case in which such party previously could have petitioned the Supreme Court for a writ of error under § <a href="/vacode/19.2-317/">19.2-317</a>. The Commonwealth may also petition the Court of Appeals for an appeal in a criminal case pursuant to § <a href="/vacode/19.2-398/">19.2-398</a>.</p><p>B. In accordance with other applicable provisions of law, appeals lie directly to the Supreme Court from a final decision, judgment or order of a circuit court involving a petition for a writ of habeas corpus, from any final finding, decision, order, or judgment of the State Corporation Commission, and from proceedings under §§ <a href="/vacode/54.1-3935/">54.1-3935</a> and <a href="/vacode/54.1-3937/">54.1-3937</a>. Complaints of the Judicial Inquiry and Review Commission shall be filed with the Supreme Court of Virginia. The Court of Appeals shall not have jurisdiction over any cases or proceedings described in this subsection.</p><p>1984, c. 701, § 17-116.05:1; 1985, c. 371; 1987, cc. 707, 710; 1988, c. 873; 1998, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?981+ful+CHAP0872" target="_blank" rel="noreferrer">872</a>; 2007, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?071+ful+CHAP0889" target="_blank" rel="noreferrer">889</a>; 2013, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?131+ful+CHAP0746" target="_blank" rel="noreferrer">746</a>; 2019, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?191+ful+CHAP0809" target="_blank" rel="noreferrer">809</a>; 2021, Sp. Sess. I, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?212+ful+CHAP0344" target="_blank" rel="noreferrer">344</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?212+ful+CHAP0345" target="_blank" rel="noreferrer">345</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?212+ful+CHAP0489" target="_blank" rel="noreferrer">489</a>.</p>',4),n=[t];function s(c,l,p,h,f,d){return a(),r("div",null,n)}const m=e(o,[["render",s]]);export{g as __pageData,m as default};
