import{_ as e,o as a,c as i,O as o}from"./chunks/framework.9482e208.js";const _=JSON.parse('{"title":"§ 17.1-410 Disposition of appeals; finality of decisions.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/17.1-410.md","filePath":"vacode/17.1-410.md"}'),t={name:"vacode/17.1-410.md"},r=o('<h1 id="§-17-1-410-disposition-of-appeals-finality-of-decisions" tabindex="-1">§ 17.1-410 Disposition of appeals; finality of decisions. <a class="header-anchor" href="#§-17-1-410-disposition-of-appeals-finality-of-decisions" aria-label="Permalink to &quot;§ 17.1-410 Disposition of appeals; finality of decisions.&quot;">​</a></h1><p>A. Each appeal of right taken to the Court of Appeals and each appeal for which a petition for appeal has been granted shall be considered by a panel of the court.</p><p>When the Court of Appeals has (i) dismissed an appeal in any case in accordance with the Rules of Court or (ii) decided an appeal, its decision shall be final, without appeal to the Supreme Court, in:</p><ol><li><p>Appeals in criminal cases pursuant to subsections A or E of § <a href="/vacode/19.2-398/">19.2-398</a> and § <a href="/vacode/19.2-401/">19.2-401</a>. Such finality of the Court of Appeals&#39; decision shall not preclude a defendant, if he is convicted, from requesting the Court of Appeals or Supreme Court on direct appeal to reconsider an issue which was the subject of the pretrial appeal; and</p></li><li><p>Appeals involving involuntary treatment of prisoners pursuant to § <a href="/vacode/53.1-40.1/">53.1-40.1</a> or <a href="/vacode/53.1-133.04/">53.1-133.04</a>.</p></li><li><p>Appeals involving denial of a concealed handgun permit pursuant to § <a href="/vacode/18.2-308.08/">18.2-308.08</a>.</p></li></ol><p>B. All other decisions of the Court of Appeals shall be appealable to the Supreme Court in accordance with the provisions of § <a href="/vacode/17.1-411/">17.1-411</a>.</p><p>1983, c. 413, § 17-116.07; 1984, c. 701; 1987, c. 710; 1988, c. 873; 1998, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?981+ful+CHAP0872" target="_blank" rel="noreferrer">872</a>; 2000, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?001+ful+CHAP0830" target="_blank" rel="noreferrer">830</a>; 2019, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?191+ful+CHAP0809" target="_blank" rel="noreferrer">809</a>; 2021, Sp. Sess. I, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?212+ful+CHAP0489" target="_blank" rel="noreferrer">489</a>.</p>',6),n=[r];function s(p,l,c,f,h,d){return a(),i("div",null,n)}const g=e(t,[["render",s]]);export{_ as __pageData,g as default};
