import{_ as e,o as a,c as t,O as o}from"./chunks/framework.9482e208.js";const _=JSON.parse('{"title":"§ 17.1-415 Compensation for judges; expenses.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/17.1-415.md","filePath":"vacode/17.1-415.md"}'),r={name:"vacode/17.1-415.md"},n=o('<h1 id="§-17-1-415-compensation-for-judges-expenses" tabindex="-1">§ 17.1-415 Compensation for judges; expenses. <a class="header-anchor" href="#§-17-1-415-compensation-for-judges-expenses" aria-label="Permalink to &quot;§ 17.1-415 Compensation for judges; expenses.&quot;">​</a></h1><p>The judges of the Court of Appeals shall receive from the Commonwealth an annual salary that shall be fixed in the general appropriations act and set at an amount equal to ninety-five percent of the annual salary fixed by law for justices of the Supreme Court. The Chief Judge of the Court of Appeals shall receive $3,000 per year in addition to the amount received by the other judges of the Court of Appeals. Each judge shall receive such amount as shall be fixed in the general appropriations act for all other expenses not otherwise reimbursed and incurred incident to the conduct of the business of the court.</p><p>1983, c. 413, § 17-116.012; 1998, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?981+ful+CHAP0872" target="_blank" rel="noreferrer">872</a>; 2001, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?011+ful+CHAP0035" target="_blank" rel="noreferrer">35</a>; 2006, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?061+ful+CHAP0218" target="_blank" rel="noreferrer">218</a>.</p>',3),s=[n];function i(l,c,p,h,f,d){return a(),t("div",null,s)}const g=e(r,[["render",i]]);export{_ as __pageData,g as default};
