import{_ as t,o as i,c as e,O as n}from"./chunks/framework.9482e208.js";const C=JSON.parse('{"title":"§ 17.1-506 Judicial circuits.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/17.1-506.md","filePath":"vacode/17.1-506.md"}'),a={name:"vacode/17.1-506.md"},l=n('<h1 id="§-17-1-506-judicial-circuits" tabindex="-1">§ 17.1-506 Judicial circuits. <a class="header-anchor" href="#§-17-1-506-judicial-circuits" aria-label="Permalink to &quot;§ 17.1-506 Judicial circuits.&quot;">​</a></h1><ol><li><p>The City of Chesapeake shall constitute the first circuit.</p></li><li><p>The City of Virginia Beach and the Counties of Accomack and Northampton shall constitute the second circuit.</p></li><li><p>The City of Portsmouth shall constitute the third circuit.</p></li><li><p>The City of Norfolk shall constitute the fourth circuit.</p></li><li><p>The Cities of Franklin and Suffolk and the Counties of Isle of Wight and Southampton shall constitute the fifth circuit.</p></li><li><p>The Cities of Emporia and Hopewell and the Counties of Brunswick, Greensville, Prince George, Surry and Sussex shall constitute the sixth circuit.</p></li><li><p>The City of Newport News shall constitute the seventh circuit.</p></li><li><p>The City of Hampton shall constitute the eighth circuit.</p></li><li><p>The Cities of Poquoson and Williamsburg and the Counties of Charles City, Gloucester, James City, King and Queen, King William, Mathews, Middlesex, New Kent and York shall constitute the ninth circuit.</p></li><li><p>The Counties of Appomattox, Buckingham, Charlotte, Cumberland, Halifax, Lunenburg, Mecklenburg and Prince Edward shall constitute the tenth circuit.</p></li><li><p>The City of Petersburg and the Counties of Amelia, Dinwiddie, Nottoway and Powhatan shall constitute the eleventh circuit.</p></li><li><p>The City of Colonial Heights and the County of Chesterfield shall constitute the twelfth circuit.</p></li><li><p>The City of Richmond shall constitute the thirteenth circuit.</p></li><li><p>The County of Henrico shall constitute the fourteenth circuit.</p></li><li><p>The City of Fredericksburg and the Counties of Caroline, Essex, Hanover, King George, Lancaster, Northumberland, Richmond, Spotsylvania, Stafford and Westmoreland shall constitute the fifteenth circuit.</p></li><li><p>The City of Charlottesville and the Counties of Albemarle, Culpeper, Fluvanna, Goochland, Greene, Louisa, Madison and Orange shall constitute the sixteenth circuit.</p></li><li><p>The County of Arlington and the City of Falls Church shall constitute the seventeenth circuit.</p></li><li><p>The City of Alexandria shall constitute the eighteenth circuit.</p></li><li><p>The City of Fairfax and the County of Fairfax shall constitute the nineteenth circuit.</p></li><li><p>The Counties of Fauquier, Loudoun and Rappahannock shall constitute the twentieth circuit.</p></li><li><p>The City of Martinsville and the Counties of Henry and Patrick shall constitute the twenty-first circuit.</p></li><li><p>The City of Danville and the Counties of Franklin and Pittsylvania shall constitute the twenty-second circuit.</p></li><li><p>The Cities of Roanoke and Salem and the County of Roanoke shall constitute the twenty-third circuit.</p></li><li><p>The City of Lynchburg and the Counties of Amherst, Bedford, Campbell and Nelson shall constitute the twenty-fourth circuit.</p></li><li><p>The Cities of Buena Vista, Covington, Lexington, Staunton and Waynesboro and the Counties of Alleghany, Augusta, Bath, Botetourt, Craig, Highland and Rockbridge shall constitute the twenty-fifth circuit.</p></li><li><p>The Cities of Harrisonburg and Winchester and the Counties of Clarke, Frederick, Page, Rockingham, Shenandoah and Warren shall constitute the twenty-sixth circuit.</p></li><li><p>The Cities of Galax and Radford and the Counties of Bland, Carroll, Floyd, Giles, Grayson, Montgomery, Pulaski and Wythe shall constitute the twenty-seventh circuit.</p></li><li><p>The City of Bristol and the Counties of Smyth and Washington shall constitute the twenty-eighth circuit.</p></li><li><p>The Counties of Buchanan, Dickenson, Russell and Tazewell shall constitute the twenty-ninth circuit.</p></li><li><p>The City of Norton and the Counties of Lee, Scott and Wise shall constitute the thirtieth circuit.</p></li><li><p>The Cities of Manassas and Manassas Park and the County of Prince William shall constitute the thirty-first circuit.</p></li></ol><p>1973, c. 544, § 17-119.1:1; 1974, c. 297; 1976, c. 126; 1977, c. 4; 1983, c. 149; 1986, c. 405; 1987, c. 624; 1991, c. 189; 1998, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?981+ful+CHAP0872" target="_blank" rel="noreferrer">872</a>; 2006, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?061+ful+CHAP0861" target="_blank" rel="noreferrer">861</a>; 2016, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?161+ful+CHAP0164" target="_blank" rel="noreferrer">164</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?161+ful+CHAP0312" target="_blank" rel="noreferrer">312</a>.</p>',3),o=[l];function h(s,r,c,u,d,p){return i(),e("div",null,o)}const g=t(a,[["render",h]]);export{C as __pageData,g as default};
