import{_ as e,o as t,c as a,O as r}from"./chunks/framework.9482e208.js";const p=JSON.parse('{"title":"§ 17.1-519 Adjournment thereof to a future day.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/17.1-519.md","filePath":"vacode/17.1-519.md"}'),o={name:"vacode/17.1-519.md"},n=r('<h1 id="§-17-1-519-adjournment-thereof-to-a-future-day" tabindex="-1">§ 17.1-519 Adjournment thereof to a future day. <a class="header-anchor" href="#§-17-1-519-adjournment-thereof-to-a-future-day" aria-label="Permalink to &quot;§ 17.1-519 Adjournment thereof to a future day.&quot;">​</a></h1><p>Whenever any judge of a circuit court has appointed a special term of any circuit court, by adjournment or warrant in the manner directed by § <a href="http://law.lis.virginia.gov/vacode/17.1-518/" target="_blank" rel="noreferrer">17.1-518</a>, and shall afterward ascertain that he cannot hold the special term on the day appointed for it, he may, by warrant, directed to the clerk of the court, adjourn it to such other day as he deems proper. The warrant shall be transmitted to the clerk, who shall immediately enter it in the order book of the court and, as to the special term thereafter to be held under the continuance, proceed in all other respects in the manner directed by § <a href="http://law.lis.virginia.gov/vacode/17.1-518/" target="_blank" rel="noreferrer">17.1-518</a>.</p><p>Code 1919, § 5895, § 17-130; 1998, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?981+ful+CHAP0872" target="_blank" rel="noreferrer">872</a>.</p>',3),d=[n];function i(c,h,l,s,u,f){return t(),a("div",null,d)}const m=e(o,[["render",i]]);export{p as __pageData,m as default};
