import{_ as o,o as n,c as i,z as e,a as t}from"./chunks/framework.9482e208.js";const _=JSON.parse('{"title":"§ 17.1-607 Security for costs upon suit by nonresident.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/17.1-607.md","filePath":"vacode/17.1-607.md"}'),s={name:"vacode/17.1-607.md"},r=e("h1",{id:"§-17-1-607-security-for-costs-upon-suit-by-nonresident",tabindex:"-1"},[t("§ 17.1-607 Security for costs upon suit by nonresident. "),e("a",{class:"header-anchor",href:"#§-17-1-607-security-for-costs-upon-suit-by-nonresident","aria-label":'Permalink to "§ 17.1-607 Security for costs upon suit by nonresident."'},"​")],-1),a=e("p",null,"In any suit or action, except when an indigent is plaintiff, there may be a suggestion on the record in court, or, if the case be at rules, on the rule docket, by a defendant, or any officer of the court, that the plaintiff is not a resident of this Commonwealth and the security is required of him. After sixty days from such suggestion, the suit or action shall, by order of the court, be dismissed, unless, before the dismissal, it is proven that plaintiff is a resident of the Commonwealth or security be given before the court, or its clerk, for the payment of the costs and damages in the court in which the suit or action is instituted which may be awarded to the defendant, and of the fees due, or to become due, in such suit or action to the officers of the court. The security shall be by bond, payable to the Commonwealth, but there need only be one obligor therein, if he be sufficient and a resident of the Commonwealth. The court before whom, or before whose clerk, such bond is given, may, on motion by a defendant or officer, give judgment for so much as he is entitled to by virtue of such bond.",-1),c=e("p",null,[t("Code 1950, § 14-182; 1964, c. 386, § 14.1-185; 1998, c. "),e("a",{href:"http://lis.virginia.gov/cgi-bin/legp604.exe?981+ful+CHAP0872",target:"_blank",rel:"noreferrer"},"872"),t(".")],-1),h=[r,a,c];fun