import{_ as e,o as t,c as a,O as o}from"./chunks/framework.9482e208.js";const _=JSON.parse('{"title":"§ 17.1-626.1 Recovery of costs in civil actions for bad checks.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/17.1-626.1.md","filePath":"vacode/17.1-626.1.md"}'),i={name:"vacode/17.1-626.1.md"},r=o('<h1 id="§-17-1-626-1-recovery-of-costs-in-civil-actions-for-bad-checks" tabindex="-1">§ 17.1-626.1 Recovery of costs in civil actions for bad checks. <a class="header-anchor" href="#§-17-1-626-1-recovery-of-costs-in-civil-actions-for-bad-checks" aria-label="Permalink to &quot;§ 17.1-626.1 Recovery of costs in civil actions for bad checks.&quot;">​</a></h1><p>A. In any civil action by a holder to recover the sum payable of a check drawn by the defendant on which payment has been refused by the payor bank because the drawer had no account or insufficient funds, or in any civil action following an arrest under § <a href="http://law.lis.virginia.gov/vacode/18.2-181/" target="_blank" rel="noreferrer">18.2-181</a> or <a href="http://law.lis.virginia.gov/vacode/18.2-182/" target="_blank" rel="noreferrer">18.2-182</a>, the court, upon a determination that the plaintiff has prevailed, shall add the following amounts, as costs, to the amount due to the plaintiff for the check: (i) the sum of $30 to defray the cost of processing the returned check; and (ii) the base wage of one employee for time actually spent acting as a witness for the Commonwealth; provided, however, that the total amount of allowable costs granted under the provisions of this section shall not exceed the sum of $250 excluding restitution for the amount of the check.</p><p>B. Such award of costs shall be contingent upon a finding (i) that the plaintiff complied with the provisions in § <a href="http://law.lis.virginia.gov/vacode/18.2-183/" target="_blank" rel="noreferrer">18.2-183</a> relating to notice and (ii) that the defendant failed to deliver payment or evidence of bank error to the plaintiff within five days after receipt of such notice.</p><p>1977, c. 329, § 6.1-118.1; 2010, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?101+ful+CHAP0343" target="_blank" rel="noreferrer">343</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?101+ful+CHAP0794" target="_blank" rel="noreferrer">794</a>.</p>',4),n=[r];function c(s,h,l,f,d,p){return t(),a("div",null,n)}const u=e(i,[["render",c]]);export{_ as __pageData,u as default};
