import{_ as e,o as t,c as o,O as i}from"./chunks/framework.9482e208.js";const p=JSON.parse('{"title":"§ 17.1-700 Composition of Council; committees.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/17.1-700.md","filePath":"vacode/17.1-700.md"}'),r={name:"vacode/17.1-700.md"},a=i('<h1 id="§-17-1-700-composition-of-council-committees" tabindex="-1">§ 17.1-700 Composition of Council; committees. <a class="header-anchor" href="#§-17-1-700-composition-of-council-committees" aria-label="Permalink to &quot;§ 17.1-700 Composition of Council; committees.&quot;">​</a></h1><p>The Judicial Council shall be established in the judiciary branch of state government and composed of 14 members consisting of the Chief Justice of the Supreme Court, one judge of the Court of Appeals, six circuit court judges, one general district court judge, one juvenile and domestic relations district court judge, two attorneys qualified to practice in the Supreme Court, and the Chairman of the House Committee for Courts of Justice and the Chairman of the Senate Committee on the Judiciary or their designees who shall be members of the Courts of Justice committees. The Council may appoint committees to aid it in the performance of its duties, and members of such committees need not be members of the Council.</p><p>1930, p. 788, § 17-222; Michie Code 1942, § 6571f; 1968, c. 387; 1972, c. 708; 1973, c. 546; 1984, c. 703; 1987, c. 141; 1998, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?981+ful+CHAP0872" target="_blank" rel="noreferrer">872</a>; 2004, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?041+ful+CHAP1000" target="_blank" rel="noreferrer">1000</a>; 2008, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?081+ful+CHAP0115" target="_blank" rel="noreferrer">115</a>.</p>',3),c=[a];function n(s,m,l,u,h,d){return t(),o("div",null,c)}const _=e(r,[["render",n]]);export{p as __pageData,_ as default};
