import{_ as e,o as t,c as i,O as o}from"./chunks/framework.9482e208.js";const g=JSON.parse('{"title":"§ 17.1-918 Transmission of certain information to Virginia State Bar, House Committee for Courts of Justice, the Senate Committee on the Judiciary, and other members of the General Assembly.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/17.1-918.md","filePath":"vacode/17.1-918.md"}'),a={name:"vacode/17.1-918.md"},r=o('<h1 id="§-17-1-918-transmission-of-certain-information-to-virginia-state-bar-house-committee-for-courts-of-justice-the-senate-committee-on-the-judiciary-and-other-members-of-the-general-assembly" tabindex="-1">§ 17.1-918 Transmission of certain information to Virginia State Bar, House Committee for Courts of Justice, the Senate Committee on the Judiciary, and other members of the General Assembly. <a class="header-anchor" href="#§-17-1-918-transmission-of-certain-information-to-virginia-state-bar-house-committee-for-courts-of-justice-the-senate-committee-on-the-judiciary-and-other-members-of-the-general-assembly" aria-label="Permalink to &quot;§ 17.1-918 Transmission of certain information to Virginia State Bar, House Committee for Courts of Justice, the Senate Committee on the Judiciary, and other members of the General Assembly.&quot;">​</a></h1><p>A. The Judicial Inquiry and Review Commission shall transmit to the appropriate District Committee of the Virginia State Bar any complaint or evidence that may come to its attention with reference to the alleged misconduct of a judge, substitute judge or pro tempore judge which relates to his private practice of law.</p><p>B. The Commission shall also transmit any evidence that it has in its possession with reference to the alleged misconduct of any judge whose election is to be considered at the next session of the General Assembly to (i) the House Committee for Courts of Justice and the Senate Committee on the Judiciary and (ii) any member of the General Assembly, upon request. Such evidence shall include the nature of the complaint, the current status of the complaint, the duration of any suspension and the evidence supporting the probable cause finding therefor, a description of any remedial course of action, and a statement concluding whether any such remedial course was successfully undertaken. A copy of any evidence in whatever form so transmitted shall be sent to the judge in question. Any such evidence transmitted to the House Committee for Courts of Justice and the Senate Committee on the Judiciary or to any member of the General Assembly shall lose its confidential character.</p><p>1973, c. 387, § 2.1-37.17:1; 1993, c. 92; 1997, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?971+ful+CHAP0914" target="_blank" rel="noreferrer">914</a>; 1998, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?981+ful+CHAP0757" target="_blank" rel="noreferrer">757</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?981+ful+CHAP0804" target="_blank" rel="noreferrer">804</a>; 2001, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?011+ful+CHAP0844" target="_blank" rel="noreferrer">844</a>; 2004, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?041+ful+CHAP0332" target="_blank" rel="noreferrer">332</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?041+ful+CHAP0363" target="_blank" rel="noreferrer">363</a>.</p>',4),n=[r];function s(c,m,h,l,f,u){return t(),i("div",null,n)}const p=e(a,[["render",s]]);export{g as __pageData,p as default};
