import{_ as e,o,c as a,O as r}from"./chunks/framework.9482e208.js";const f=JSON.parse('{"title":"§ 18.2-102.2 Unauthorized use of dairy milk cases or milk crates; penalty.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/18.2-102.2.md","filePath":"vacode/18.2-102.2.md"}'),t={name:"vacode/18.2-102.2.md"},i=r('<h1 id="§-18-2-102-2-unauthorized-use-of-dairy-milk-cases-or-milk-crates-penalty" tabindex="-1">§ 18.2-102.2 Unauthorized use of dairy milk cases or milk crates; penalty. <a class="header-anchor" href="#§-18-2-102-2-unauthorized-use-of-dairy-milk-cases-or-milk-crates-penalty" aria-label="Permalink to &quot;§ 18.2-102.2 Unauthorized use of dairy milk cases or milk crates; penalty.&quot;">​</a></h1><p>It shall be unlawful for any person to:</p><ol><li><p>Buy, sell, or dispose of any milk case or milk crate bearing the name or label of the owner without the written consent of the owner or his designated agent;</p></li><li><p>Refuse, upon written demand of the owner or his designated agent, to return to the owner or his designated agent any milk case or milk crate bearing the name or label of the owner; or</p></li><li><p>Deface, obliterate, erase, cover up, or otherwise remove or conceal any name, label, registered trademark, insignia, or other business identification of an owner of a milk case or milk crate without the consent of the owner, for the purpose of destroying or removing from the milk case or milk crate evidence of its ownership.</p></li></ol><p>A violation of this section shall be punishable as a Class 4 misdemeanor.</p><p>For purposes of this section, milk cases or milk crates shall be deemed to bear a name or label of an owner when there is imprinted or attached on the case or crate a name, insignia, mark, business identification, or label showing ownership or sufficient information to ascertain ownership. The term &quot;milk case&quot; or &quot;milk crate&quot; means a wire or plastic container which holds sixteen quarts or more of beverage and is used by distributors or retailers or their agents as a means to transport, store, or carry dairy products.</p><p>1990, c. 452.</p>',6),s=[i];function n(l,c,d,h,m,p){return o(),a("div",null,s)}const _=e(t,[["render",n]]);export{f as __pageData,_ as default};
