import{_ as e,o,c as n,O as t}from"./chunks/framework.9482e208.js";const _=JSON.parse('{"title":"§ 18.2-11 Punishment for conviction of misdemeanor.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/18.2-11.md","filePath":"vacode/18.2-11.md"}'),i={name:"vacode/18.2-11.md"},a=t('<h1 id="§-18-2-11-punishment-for-conviction-of-misdemeanor" tabindex="-1">§ 18.2-11 Punishment for conviction of misdemeanor. <a class="header-anchor" href="#§-18-2-11-punishment-for-conviction-of-misdemeanor" aria-label="Permalink to &quot;§ 18.2-11 Punishment for conviction of misdemeanor.&quot;">​</a></h1><p>The authorized punishments for conviction of a misdemeanor are:</p><p>(a) For Class 1 misdemeanors, confinement in jail for not more than twelve months and a fine of not more than $2,500, either or both.</p><p>(b) For Class 2 misdemeanors, confinement in jail for not more than six months and a fine of not more than $1,000, either or both.</p><p>(c) For Class 3 misdemeanors, a fine of not more than $500.</p><p>(d) For Class 4 misdemeanors, a fine of not more than $250.</p><p>For a misdemeanor offense prohibiting proximity to children as described in subsection A of § <a href="http://law.lis.virginia.gov/vacode/18.2-370.2/" target="_blank" rel="noreferrer">18.2-370.2</a>, the sentencing court is authorized to impose the punishment set forth in subsection B of that section in addition to any other penalty provided by law.</p><p>1975, cc. 14, 15; 1990, c. 788; 2000, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?001+ful+CHAP0770" target="_blank" rel="noreferrer">770</a>.</p>',8),r=[a];function s(c,m,h,d,f,p){return o(),n("div",null,r)}const u=e(i,[["render",s]]);export{_ as __pageData,u as default};
