import{_ as e,o as t,c as o,O as a}from"./chunks/framework.9482e208.js";const y=JSON.parse('{"title":"§ 18.2-112.1 Misuse of public assets; penalty.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/18.2-112.1.md","filePath":"vacode/18.2-112.1.md"}'),i={name:"vacode/18.2-112.1.md"},s=a('<h1 id="§-18-2-112-1-misuse-of-public-assets-penalty" tabindex="-1">§ 18.2-112.1 Misuse of public assets; penalty. <a class="header-anchor" href="#§-18-2-112-1-misuse-of-public-assets-penalty" aria-label="Permalink to &quot;§ 18.2-112.1 Misuse of public assets; penalty.&quot;">​</a></h1><p>A. For purposes of this section, &quot;public assets&quot; means personal property belonging to or paid for by the Commonwealth, or any city, town, county, or any other political subdivision, or the labor of any person other than the accused that is paid for by the Commonwealth, or any city, town, county, or any other political subdivision.</p><p>B. Any full-time officer, agent, or employee of the Commonwealth, or of any city, town, county, or any other political subdivision who, without lawful authorization, uses or permits the use of public assets for private or personal purposes unrelated to the duties and office of the accused or any other legitimate government interest when the value of such use exceeds $1,000 in any 12-month period, is guilty of a Class 4 felony.</p><p>C. Any county, city, or town shall be permitted to adopt a local ordinance that provides that any non-full-time officer, agent, employee, or elected official of the county, city, or town who, without lawful authorization, uses or permits the use of public assets for private or personal purposes unrelated to the duties and office of the accused or any other legitimate government interest when the value of such use exceeds $1,000 in any 12-month period is guilty of a Class 1 misdemeanor.</p><p>2008, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?081+ful+CHAP0738" target="_blank" rel="noreferrer">738</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?081+ful+CHAP0755" target="_blank" rel="noreferrer">755</a>; 2014, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?141+ful+CHAP0321" target="_blank" rel="noreferrer">321</a>.</p>',5),r=[s];function n(l,c,p,u,h,f){return t(),o("div",null,r)}const m=e(i,[["render",n]]);export{y as __pageData,m as default};
