import{_ as e,o,c as t,O as a}from"./chunks/framework.9482e208.js";const m=JSON.parse('{"title":"§ 18.2-115.1 Unlawful sublease of a motor vehicle; penalty.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/18.2-115.1.md","filePath":"vacode/18.2-115.1.md"}'),r={name:"vacode/18.2-115.1.md"},s=a('<h1 id="§-18-2-115-1-unlawful-sublease-of-a-motor-vehicle-penalty" tabindex="-1">§ 18.2-115.1 Unlawful sublease of a motor vehicle; penalty. <a class="header-anchor" href="#§-18-2-115-1-unlawful-sublease-of-a-motor-vehicle-penalty" aria-label="Permalink to &quot;§ 18.2-115.1 Unlawful sublease of a motor vehicle; penalty.&quot;">​</a></h1><p>A. It shall be unlawful for any person, for profit in the course of business, who is not a party to a lease contract, conditional sales contract, or security agreement which transfers any right or interest in a motor vehicle, knowing that the motor vehicle is subject to a lease, security interest or lien, to:</p><ol><li><p>Obtain or exercise control over a motor vehicle and sell, transfer, assign, or lease the motor vehicle to another person without the prior written authorization of the secured creditor, lessor, or lienholder if he receives compensation or other consideration for the sale, transfer, assignment, or lease of the motor vehicle; or</p></li><li><p>Assist, cause, or arrange the actual or purported sale, transfer, assignment, or lease of a motor vehicle to another person without the prior written authorization of the secured creditor, lessor, or lienholder if he receives compensation or other consideration for assisting, causing, or arranging the sale, transfer, assignment, or lease of the motor vehicle.</p></li></ol><p>B. A violation of this section is punishable as a Class 3 misdemeanor.</p><p>C. This section shall not apply to any employee acting upon request of his employer.</p><p>D. This section shall not apply if the entire indebtedness owed under or secured by the lease, conditional sales contract, or security agreement through the date of payment is paid in full and received by the lessor or secured party within thirty days after the sale, transfer, assignment, or lease of the motor vehicle.</p><p>1990, c. 844; 1993, c. 608.</p>',7),n=[s];function i(l,c,h,p,f,u){return o(),t("div",null,n)}const _=e(r,[["render",i]]);export{m as __pageData,_ as default};
