import{_ as e,o as a,c as t,O as n}from"./chunks/framework.9482e208.js";const y=JSON.parse('{"title":"§ 18.2-121.3 Trespass with an unmanned aircraft system; penalty.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/18.2-121.3.md","filePath":"vacode/18.2-121.3.md"}'),r={name:"vacode/18.2-121.3.md"},s=n('<h1 id="§-18-2-121-3-trespass-with-an-unmanned-aircraft-system-penalty" tabindex="-1">§ 18.2-121.3 Trespass with an unmanned aircraft system; penalty. <a class="header-anchor" href="#§-18-2-121-3-trespass-with-an-unmanned-aircraft-system-penalty" aria-label="Permalink to &quot;§ 18.2-121.3 Trespass with an unmanned aircraft system; penalty.&quot;">​</a></h1><p>A. Any person who knowingly and intentionally causes an unmanned aircraft system to (i) enter the property of another and come within 50 feet of a dwelling house (a) to coerce, intimidate, or harass another person or (b) after having been given actual notice to desist, for any other reason, or (ii) take off or land in violation of current Federal Aviation Administration Special Security Instructions or UAS Security Sensitive Airspace Restrictions is guilty of a Class 1 misdemeanor.</p><p>B. This section shall not apply to any person who causes an unmanned aircraft system to enter the property as set forth in subsection A if (i) consent is given to the entry by any person with legal authority to consent or by any person who is lawfully present on such property or (ii) such person is authorized by federal regulations to operate an unmanned aircraft system and is operating such system in an otherwise lawful manner and consistent with federal regulations.</p><p>2018, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?181+ful+CHAP0851" target="_blank" rel="noreferrer">851</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?181+ful+CHAP0852" target="_blank" rel="noreferrer">852</a>; 2019, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?191+ful+CHAP0612" target="_blank" rel="noreferrer">612</a>.</p>',4),i=[s];function o(c,l,p,h,d,f){return a(),t("div",null,i)}const m=e(r,[["render",o]]);export{y as __pageData,m as default};
