import{_ as e,o,c as r,O as t}from"./chunks/framework.9482e208.js";const _=JSON.parse('{"title":"§ 18.2-128 Trespass upon church or school property.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/18.2-128.md","filePath":"vacode/18.2-128.md"}'),a={name:"vacode/18.2-128.md"},n=t('<h1 id="§-18-2-128-trespass-upon-church-or-school-property" tabindex="-1">§ 18.2-128 Trespass upon church or school property. <a class="header-anchor" href="#§-18-2-128-trespass-upon-church-or-school-property" aria-label="Permalink to &quot;§ 18.2-128 Trespass upon church or school property.&quot;">​</a></h1><p>A. Any person who, without the consent of some person authorized to give such consent, goes or enters upon, in the nighttime, the premises or property of any church or upon any school property for any purpose other than to attend a meeting or service held or conducted in such church or school property, shall be guilty of a Class 3 misdemeanor.</p><p>B. It shall be unlawful for any person, whether or not a church member or student, to enter upon or remain upon any church or school property in violation of (i) any direction to vacate the property by a person authorized to give such direction or (ii) any posted notice which contains such information, posted at a place where it reasonably may be seen. Each time such person enters upon or remains on the posted premises or after such direction that person refuses to vacate such property, it shall constitute a separate offense.</p><p>A violation of this subsection shall be punishable as a Class 1 misdemeanor, except that any person, other than a parent, who violates this subsection on school property with the intent to abduct a student shall be guilty of a Class 6 felony.</p><p>C. For purposes of this section: (i) &quot;school property&quot; includes a school bus as defined in § <a href="http://law.lis.virginia.gov/vacode/46.2-100/" target="_blank" rel="noreferrer">46.2-100</a> and (ii) &quot;church&quot; means any place of worship and includes any educational building or community center owned or leased by a church.</p><p>Code 1950, § 18.1-182; 1960, c. 358; 1975, cc. 14, 15; 1988, c. 497; 1989, c. 680; 1993, c. 961; 1994, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?941+ful+CHAP0326" target="_blank" rel="noreferrer">326</a>; 1995, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?951+ful+CHAP0493" target="_blank" rel="noreferrer">493</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?951+ful+CHAP0642" target="_blank" rel="noreferrer">642</a>; 1997, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?971+ful+CHAP0779" target="_blank" rel="noreferrer">779</a>.</p>',6),s=[n];function i(c,h,p,l,u,d){return o(),r("div",null,s)}const g=e(a,[["render",i]]);export{_ as __pageData,g as default};
