import{_ as e,o as r,c as t,O as a}from"./chunks/framework.9482e208.js";const u=JSON.parse('{"title":"§ 18.2-145.1 Damaging or destroying research farm product; penalty; restitution.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/18.2-145.1.md","filePath":"vacode/18.2-145.1.md"}'),o={name:"vacode/18.2-145.1.md"},i=a('<h1 id="§-18-2-145-1-damaging-or-destroying-research-farm-product-penalty-restitution" tabindex="-1">§ 18.2-145.1 Damaging or destroying research farm product; penalty; restitution. <a class="header-anchor" href="#§-18-2-145-1-damaging-or-destroying-research-farm-product-penalty-restitution" aria-label="Permalink to &quot;§ 18.2-145.1 Damaging or destroying research farm product; penalty; restitution.&quot;">​</a></h1><p>A. Any person or entity that (i) maliciously damages or destroys any farm product, as defined in § <a href="/vacode/3.2-4709/">3.2-4709</a>, and (ii) knows the product is grown for testing or research purposes in the context of product development in conjunction or coordination with a private research facility or a baccalaureate institution of higher education or any federal, state, or local government agency is guilty of a Class 1 misdemeanor if the value of the farm product was less than $1,000, or a Class 6 felony if the value of the farm product was $1,000 or more.</p><p>B. The court shall order the defendant to make restitution in accordance with § <a href="/vacode/19.2-305.1/">19.2-305.1</a> for the damage or destruction caused. For the purpose of awarding restitution under this section, the court shall determine the market value of the farm product prior to its damage or destruction and, in so doing, shall include the cost of: (i) production, (ii) research, (iii) testing, (iv) replacement, and (v) product development directly related to the product damaged or destroyed.</p><p>2001, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?011+ful+CHAP0547" target="_blank" rel="noreferrer">547</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?011+ful+CHAP0572" target="_blank" rel="noreferrer">572</a>; 2018, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?181+ful+CHAP0764" target="_blank" rel="noreferrer">764</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?181+ful+CHAP0765" target="_blank" rel="noreferrer">765</a>; 2020, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?201+ful+CHAP0089" target="_blank" rel="noreferrer">89</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?201+ful+CHAP0401" target="_blank" rel="noreferrer">401</a>.</p>',4),n=[i];function s(c,d,l,g,h,p){return r(),t("div",null,n)}const m=e(o,[["render",s]]);export{u as __pageData,m as default};
