import{_ as e,o as a,c as r,O as t}from"./chunks/framework.9482e208.js";const g=JSON.parse('{"title":"§ 18.2-152.7 Personal trespass by computer; penalty.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/18.2-152.7.md","filePath":"vacode/18.2-152.7.md"}'),s={name:"vacode/18.2-152.7.md"},o=t('<h1 id="§-18-2-152-7-personal-trespass-by-computer-penalty" tabindex="-1">§ 18.2-152.7 Personal trespass by computer; penalty. <a class="header-anchor" href="#§-18-2-152-7-personal-trespass-by-computer-penalty" aria-label="Permalink to &quot;§ 18.2-152.7 Personal trespass by computer; penalty.&quot;">​</a></h1><p>A. A person is guilty of the crime of personal trespass by computer when he uses a computer or computer network to cause physical injury to an individual.</p><p>B. If committed maliciously, the crime of personal trespass by computer shall be punishable as a Class 3 felony. If such act is done unlawfully but not maliciously, the crime of personal trespass by computer shall be punishable as a Class 6 felony.</p><p>1984, c. 751; 1985, c. 322; 2003, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?031+ful+CHAP0987" target="_blank" rel="noreferrer">987</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?031+ful+CHAP1016" target="_blank" rel="noreferrer">1016</a>; 2005, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?051+ful+CHAP0746" target="_blank" rel="noreferrer">746</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?051+ful+CHAP0761" target="_blank" rel="noreferrer">761</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?051+ful+CHAP0827" target="_blank" rel="noreferrer">827</a>.</p>',4),l=[o];function n(i,p,c,u,_,f){return a(),r("div",null,l)}const m=e(s,[["render",n]]);export{g as __pageData,m as default};
