import{_ as o,o as t,c as i,z as e,a}from"./chunks/framework.9482e208.js";const g=JSON.parse('{"title":"§ 18.2-165.1 Tampering with or unlawful use of cable television service.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/18.2-165.1.md","filePath":"vacode/18.2-165.1.md"}'),n={name:"vacode/18.2-165.1.md"},s=e("h1",{id:"§-18-2-165-1-tampering-with-or-unlawful-use-of-cable-television-service",tabindex:"-1"},[a("§ 18.2-165.1 Tampering with or unlawful use of cable television service. "),e("a",{class:"header-anchor",href:"#§-18-2-165-1-tampering-with-or-unlawful-use-of-cable-television-service","aria-label":'Permalink to "§ 18.2-165.1 Tampering with or unlawful use of cable television service."'},"​")],-1),r=e("p",null,"Any person who (i) shall knowingly obtain or attempt to obtain cable television service from another by means, artifice, trick, deception or device without the payment to the operator of such service of all lawful compensation for each type of service obtained; (ii) shall knowingly, and with intent to profit thereby from any consideration received or expected, assist or instruct any other person in obtaining or attempting to obtain any cable television service without the payment to the operator of said service of all lawful compensation; (iii) shall knowingly tamper or otherwise interfere with or connect to by any means whether mechanical, electrical, acoustical or other, any cables, wires, or other devices used for the distribution of cable television service without authority from the operator of such service; or (iv) shall knowingly sell, rent, lend, promote, offer or advertise for sale, rental or use any device of any description or any plan for making or assembling the same to any person, with knowledge that the person intends to use such device or plan to do any of the acts hereinbefore mentioned or if the device or plan was represented either directly or indirectly by the person distributing it as having the ability to facilitate the doing of any of the acts hereinbefore mentioned, shall be guilty of a Class 6 felony if convicted under clause (ii) or (iv) above and shall be guilty of a Class 1 misdemeanor if convicted under clause (i) or (iii) above.",-1),c=e("p",null,"As used herein, cable television service shall include any and all services provided by or through the facilities of any cable television system or closed circuit coaxial cable communications system or any microwave, satellite or similar transmission service used in connection with any cable television system or other similar closed circuit coaxial cable communications system.",-1),l=e("p",null,"In any prosecution under this section, the existence on property in the actual possession of the accused, of any connection, wire, conductor, or any device whatsoever, which permits the use of cable television service without the same being reported for payment to and specifically authorized by the operator of the cable television service shall be prima facie evidence of intent to violate and of the violation of this section by the accused.",-1),h=e("p",null,"Nothing contained in this section shall be construed so as to abrogate or interfere with any contract right or remedy of any person having a contract with the owner of a television coaxial cable, or a cablevision system, or a microwave radio system.",-1),d=e("p",null,"1978, c. 712; 1979, c. 500; 1981, c. 197; 1991, c. 502.",-1),f=[s,r,c,l,h,d];function u(v,p,m,y,b,w){return t(),i("div",null,f)}const k=o(n,[["render",u]]);export{g as __pageData,k as default};
