import{_ as e,o as a,c as r,O as o}from"./chunks/framework.9482e208.js";const m=JSON.parse('{"title":"§ 18.2-174 Impersonating law-enforcement officer; penalty.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/18.2-174.md","filePath":"vacode/18.2-174.md"}'),t={name:"vacode/18.2-174.md"},n=o('<h1 id="§-18-2-174-impersonating-law-enforcement-officer-penalty" tabindex="-1">§ 18.2-174 Impersonating law-enforcement officer; penalty. <a class="header-anchor" href="#§-18-2-174-impersonating-law-enforcement-officer-penalty" aria-label="Permalink to &quot;§ 18.2-174 Impersonating law-enforcement officer; penalty.&quot;">​</a></h1><p>Any person who falsely assumes or exercises the functions, powers, duties, and privileges incident to the office of sheriff, police officer, marshal, or other peace officer, or any local, city, county, state, or federal law-enforcement officer, or who falsely assumes or pretends to be any such officer, is guilty of a Class 1 misdemeanor. A second or subsequent offense is punishable as a Class 6 felony.</p><p>Code 1950, § 18.1-311; 1960, c. 358; 1975, cc. 14, 15; 2013, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?131+ful+CHAP0410" target="_blank" rel="noreferrer">410</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?131+ful+CHAP0431" target="_blank" rel="noreferrer">431</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?131+ful+CHAP0638" target="_blank" rel="noreferrer">638</a>.</p>',3),i=[n];function s(c,f,l,p,_,d){return a(),r("div",null,i)}const g=e(t,[["render",s]]);export{m as __pageData,g as default};
