import{_ as e,o as t,c as a,O as o}from"./chunks/framework.9482e208.js";const _=JSON.parse('{"title":"§ 18.2-178.1 Financial exploitation of vulnerable adults; penalty.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/18.2-178.1.md","filePath":"vacode/18.2-178.1.md"}'),n={name:"vacode/18.2-178.1.md"},i=o('<h1 id="§-18-2-178-1-financial-exploitation-of-vulnerable-adults-penalty" tabindex="-1">§ 18.2-178.1 Financial exploitation of vulnerable adults; penalty. <a class="header-anchor" href="#§-18-2-178-1-financial-exploitation-of-vulnerable-adults-penalty" aria-label="Permalink to &quot;§ 18.2-178.1 Financial exploitation of vulnerable adults; penalty.&quot;">​</a></h1><p>A. As used in this section, &quot;vulnerable adult&quot; means the same as that term is defined in § <a href="/vacode/18.2-369/">18.2-369</a>.</p><p>B. It is unlawful for any person who knows or should know that another person is a vulnerable adult to, through the use of that other person&#39;s impairment, take, obtain, or convert money or other thing of value belonging to that other person with the intent to permanently deprive him thereof. Any person who violates this section shall be deemed guilty of larceny.</p><p>C. Venue for the trial of an accused charged with a violation of this section shall be in any county or city in which (i) any act was performed in furtherance of the offense or (ii) the accused resided at the time of the offense.</p><p>D. This section shall not apply to a transaction or disposition of money or other thing of value in which the accused acted for the benefit of the vulnerable adult or made a good faith effort to assist such person with the management of his money or other thing of value.</p><p>2013, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?131+ful+CHAP0419" target="_blank" rel="noreferrer">419</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?131+ful+CHAP0452" target="_blank" rel="noreferrer">452</a>; 2022, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?221+ful+CHAP0259" target="_blank" rel="noreferrer">259</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?221+ful+CHAP0642" target="_blank" rel="noreferrer">642</a>.</p>',6),r=[i];function l(s,h,c,f,p,d){return t(),a("div",null,r)}const g=e(n,[["render",l]]);export{_ as __pageData,g as default};
