import{_ as e,o as r,c as o,O as a}from"./chunks/framework.9482e208.js";const g=JSON.parse('{"title":"§ 18.2-182 Issuing bad checks on behalf of business firm or corporation in payment of wages; penalty.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/18.2-182.md","filePath":"vacode/18.2-182.md"}'),n={name:"vacode/18.2-182.md"},t=a('<h1 id="§-18-2-182-issuing-bad-checks-on-behalf-of-business-firm-or-corporation-in-payment-of-wages-penalty" tabindex="-1">§ 18.2-182 Issuing bad checks on behalf of business firm or corporation in payment of wages; penalty. <a class="header-anchor" href="#§-18-2-182-issuing-bad-checks-on-behalf-of-business-firm-or-corporation-in-payment-of-wages-penalty" aria-label="Permalink to &quot;§ 18.2-182 Issuing bad checks on behalf of business firm or corporation in payment of wages; penalty.&quot;">​</a></h1><p>Any person who shall make, draw, or utter, or deliver any check, draft, or order for the payment of money, upon any bank, banking institution, trust company or other depository on behalf of any business firm or corporation, for the purpose of paying wages to any employee of such firm or corporation, or for the purpose of paying for any labor performed by any person for such firm or corporation, knowing, at the time of such making, drawing, uttering or delivering, that the account upon which such check, draft or order is drawn has not sufficient funds, or credit with, such bank, banking institution, trust company or other depository, for the payment of such check, draft or order, although no express representation is made in reference thereto, shall be guilty of a Class 1 misdemeanor; except that if this check, draft, or order has a represented value of $1,000 or more, such person shall be guilty of a Class 6 felony.</p><p>The word &quot;credit,&quot; as used herein, shall be construed to mean any arrangement or understanding with the bank, banking institution, trust company, or other depository for the payment of such check, draft or order.</p><p>In addition to the criminal penalty set forth herein, such person shall be personally liable in any civil action brought upon such check, draft or order.</p><p>Code 1950, § 6.1-116; 1966, c. 584; 1975, cc. 14, 15; 2005, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?051+ful+CHAP0598" target="_blank" rel="noreferrer">598</a>; 2018, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?181+ful+CHAP0764" target="_blank" rel="noreferrer">764</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?181+ful+CHAP0765" target="_blank" rel="noreferrer">765</a>; 2020, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?201+ful+CHAP0089" target="_blank" rel="noreferrer">89</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?201+ful+CHAP0401" target="_blank" rel="noreferrer">401</a>.</p>',5),i=[t];function s(c,h,f,l,p,d){return r(),o("div",null,i)}const b=e(n,[["render",s]]);export{g as __pageData,b as default};
